import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/css/BudgetAllocation.css";

const BudgetAllocation = () => {
  const [budgetList, setBudgetList] = useState([]);
  const [totalIncome, setTotalIncome] = useState(0);
  const [totalBudgetAllocated, setTotalBudgetAllocated] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [newBudgetAllocated, setNewBudgetAllocated] = useState("");
  const [message, setMessage] = useState("");

  const categories = ["Groceries", "Rent", "Utilities", "Entertainment", "Savings", "Miscellaneous"];

  // Fetch income and budget data when the component loads
  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          setMessage("Please log in to see your budget data.");
          return;
        }

        const incomeResponse = await axios.get("http://localhost:3001/api/income", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTotalIncome(incomeResponse.data.totalIncome);

        const budgetResponse = await axios.get("http://localhost:3001/api/budget", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setBudgetList(budgetResponse.data.budgets);
        setTotalBudgetAllocated(
          budgetResponse.data.budgets.reduce((total, budget) => total + budget.budgetAllocated, 0)
        );
      } catch (err) {
        setMessage("Error fetching data.");
      }
    };

    fetchData();
  }, []);

  const handleAddBudget = async () => {
    if (!selectedCategory || !newBudgetAllocated) {
      setMessage("Please fill out both fields.");
      return;
    }

    const newAllocation = parseFloat(newBudgetAllocated);
    if (newAllocation + totalBudgetAllocated > totalIncome) {
      setMessage("Budget allocated cannot exceed total income.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("Please log in to add a budget.");
        return;
      }

      await axios.post(
        "http://localhost:3001/api/budget",
        { category: selectedCategory, budgetAllocated: newAllocation },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setMessage("Budget added successfully!");
      setSelectedCategory("");
      setNewBudgetAllocated("");
      const response = await axios.get("http://localhost:3001/api/budget", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setBudgetList(response.data.budgets);
      setTotalBudgetAllocated(
        response.data.budgets.reduce((total, budget) => total + budget.budgetAllocated, 0)
      );
    } catch (err) {
      setMessage("Error adding budget.");
    }
  };

  const handleRemoveBudget = async (id) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("Please log in to remove a budget.");
        return;
      }

      await axios.delete(`http://localhost:3001/api/budget/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const updatedBudgetList = budgetList.filter((item) => item._id !== id);
      setBudgetList(updatedBudgetList);
      setTotalBudgetAllocated(
        updatedBudgetList.reduce((total, budget) => total + budget.budgetAllocated, 0)
      );
      setMessage("Budget removed successfully!");
    } catch (err) {
      setMessage("Error removing budget.");
    }
  };

  const handleUpdateBudget = async (id, newAllocation) => {
    if (newAllocation + totalBudgetAllocated > totalIncome) {
      setMessage("Budget allocated cannot exceed total income.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("Please log in to update a budget.");
        return;
      }

      await axios.put(
        `http://localhost:3001/api/budget/${id}`,
        { budgetAllocated: newAllocation },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      const response = await axios.get("http://localhost:3001/api/budget", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setBudgetList(response.data.budgets);
      setTotalBudgetAllocated(
        response.data.budgets.reduce((total, budget) => total + budget.budgetAllocated, 0)
      );
      setMessage("Budget updated successfully!");
    } catch (err) {
      setMessage("Error updating budget.");
    }
  };

  return (
    <div className="budget-container">
      <h2>Budget Allocation</h2>
      <div className="budget-summary">
        <h3>Total Income: ₹{totalIncome}</h3>
        <h3>Total Budget Allocated: ₹{totalBudgetAllocated}</h3>
        <h3>Budget Remaining: ₹{totalIncome - totalBudgetAllocated}</h3>
      </div>
      {message && <p className="message">{message}</p>}

      <p>Add Budget</p>
      <div className="add-budget">
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          <option value="">Select Category</option>
          {categories.map((category, index) => (
            <option key={index} value={category}>
              {category}
            </option>
          ))}
        </select>
        <input
          type="number"
          value={newBudgetAllocated}
          onChange={(e) => setNewBudgetAllocated(e.target.value)}
          placeholder="Allocated Budget"
        />
        <button onClick={handleAddBudget}>Add Budget</button>
      </div>

      <p>Budget List</p>
      <ul className="budget-list">
        {budgetList.map((item) => (
          <li key={item._id} className="budget-item">
            <span>
              {item.category}: ₹{item.budgetAllocated} (Remaining: ₹
              {item.remainingBudget})
            </span>
            <span>Date: {new Date(item.date).toLocaleDateString()}</span>
            <input
              type="number"
              defaultValue={item.budgetAllocated}
              onBlur={(e) => handleUpdateBudget(item._id, parseFloat(e.target.value))}
            />
            <button onClick={() => handleRemoveBudget(item._id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BudgetAllocation;
