import React, { useState, useEffect } from "react";
import axios from "axios";
import { Line } from "react-chartjs-2";
import '../assets/css/Reports.css';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from "chart.js";

// Register ChartJS components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const Reports = () => {
  const [reportData, setReportData] = useState({
    totalIncome: 0,
    budgetAllocated: 0,
    totalExpense: 0,
    incomeGraphs: { labels: [], datasets: [{ label: "Income", data: [] }] },
    budgetGraphs: { labels: [], datasets: [{ label: "Allocated", data: [] }, { label: "Remaining", data: [] }] },
    expenseGraphs: { labels: [], datasets: [{ label: "Expense", data: [] }] },
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchReportData = async () => {
      setLoading(true);
      setError("");

      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:3001/api/reports", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const { totalIncome, budgetAllocated, totalExpense, incomeGraphs, budgetGraphs, expenseGraphs } = response.data;

        setReportData({
          totalIncome,
          budgetAllocated,
          totalExpense,
          incomeGraphs,
          budgetGraphs,
          expenseGraphs,
        });
      } catch (err) {
        setError("Failed to load report data. Please try again later.");
        console.error("Error:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchReportData();
  }, []);

  const graphOptions = {
    responsive: true,
    plugins: { title: { display: true, text: "" } },
    scales: { x: { beginAtZero: true }, y: { beginAtZero: true } },
  };

  if (loading) return <div>Loading report data...</div>;
  if (error) return <div>{error}</div>;

  const renderGraph = (data, title) => {
    if (!data.labels.length || !data.datasets.length) {
      return <div>No data available</div>;
    }
    return <Line data={data} options={{ ...graphOptions, plugins: { title: { text: title } } }} />;
  };

  return (
    <div className="report-container">
      <div className="report-summary">
        {["Total Income", "Budget Allocated", "Budget Remaining", "Total Expense", "Amount Left to Spend"].map(
          (label, index) => {
            const values = [
              reportData.totalIncome,
              reportData.budgetAllocated,
              reportData.totalIncome - reportData.budgetAllocated,
              reportData.totalExpense,
              reportData.totalIncome - reportData.totalExpense,
            ];
            return (
              <div className="summary-box" key={index}>
                <h3>{label}</h3>
                <p>{values[index]}</p>
              </div>
            );
          }
        )}
      </div>

      <div className="graphs-container">
        {["Income by Source", "Income by Date"].map((title, index) => (
          <div className="graph-box" key={index}>
            {renderGraph(reportData.incomeGraphs, title)}
          </div>
        ))}
      </div>

      <div className="graphs-container">
        {["Budget Allocation by Category", "Budget Remaining by Category"].map((title, index) => (
          <div className="graph-box" key={index}>
            {renderGraph(reportData.budgetGraphs, title)}
          </div>
        ))}
      </div>

      <div className="graphs-container">
        {["Expense by Category", "Expense by Date"].map((title, index) => (
          <div className="graph-box" key={index}>
            {renderGraph(reportData.expenseGraphs, title)}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Reports;
