import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/css/BudgetAllocation.css";

const BudgetAllocation = () => {
  const [categories, setCategories] = useState([]);
  const [totalIncome, setTotalIncome] = useState(0);
  const [allocatedBudget, setAllocatedBudget] = useState(0);
  const [newCategory, setNewCategory] = useState("");
  const [newBudget, setNewBudget] = useState("");
  const [message, setMessage] = useState("");

  // Fetch budget data from backend
  useEffect(() => {
    const fetchBudgetData = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          setMessage("Please log in to access budget allocation.");
          return;
        }

        const response = await axios.get("http://localhost:3001/api/budget", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setCategories(response.data.categories);
        setTotalIncome(response.data.totalIncome);
        setAllocatedBudget(response.data.allocatedBudget);
      } catch (err) {
        setMessage("Error fetching budget data.");
      }
    };

    fetchBudgetData();
  }, []);

  // Add a new budget category
  const handleAddCategory = async () => {
    if (!newCategory || !newBudget || newBudget <= 0) {
      setMessage("Please enter a valid category and budget.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("Please log in to add a category.");
        return;
      }

      const response = await axios.post(
        "http://localhost:3001/api/budget",
        { category: newCategory, budget: parseFloat(newBudget) },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setCategories(response.data.categories);
      setAllocatedBudget(response.data.allocatedBudget);
      setNewCategory("");
      setNewBudget("");
      setMessage("Category added successfully!");
    } catch (err) {
      setMessage("Error adding category.");
    }
  };

  // Remove a budget category
  const handleRemoveCategory = async (id) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("Please log in to remove a category.");
        return;
      }

      const response = await axios.delete(`http://localhost:3001/api/budget/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setCategories(response.data.categories);
      setAllocatedBudget(response.data.allocatedBudget);
      setMessage("Category removed successfully!");
    } catch (err) {
      setMessage("Error removing category.");
    }
  };

  // Calculate remaining budget
  const remainingBudget = totalIncome - allocatedBudget;

  return (
    <div className="budget-container">
      <h2>Budget Allocation</h2>
      {message && <p className="message">{message}</p>}

      <div className="budget-summary">
        <h3>Total Income: ₹{totalIncome}</h3>
        <h3>Allocated Budget: ₹{allocatedBudget}</h3>
        <h3>Remaining Budget: ₹{remainingBudget}</h3>
      </div>

      <p>Add a Budget Category</p>
      <div className="add-category">
        <input
          type="text"
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
          placeholder="Category Name"
        />
        <input
          type="number"
          value={newBudget}
          onChange={(e) => setNewBudget(e.target.value)}
          placeholder="Budget Amount"
        />
        <button onClick={handleAddCategory}>Add Category</button>
      </div>

      <p>Categories</p>
      <ul className="category-list">
        {categories.map((item) => (
          <li key={item._id} className="category-item">
            <span>{item.category}: ₹{item.budget}</span>
            <button onClick={() => handleRemoveCategory(item._id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BudgetAllocation;
