import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import styles from "../assets/css/Auth.module.css";
import logo from '../assets/images/logo.png'

const ForgotPassword = () => {
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!email) {
      setMessage("Please enter a valid email address.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:3001/api/auth/forgot-password", { email });
      setMessage(response.data.message || "Password reset instructions sent!");
      navigate("/login");
    } catch (error) {
      setMessage(error.response?.data?.message || "Failed to send reset instructions.");
    }
  };

  return (
    <div className={styles.authContainer}>
      <img src={logo} alt="Logo" className={styles.authlogo}/>
      <h2>Forgot Password</h2>
      <form onSubmit={handleSubmit} className={styles.authForm}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Enter your email"
          className={styles.authInput}
        />
        <button type="submit" className={styles.authButton}>
          Submit
        </button>
      </form>
      {message && <p className={styles.message}>{message}</p>}

      <div className={styles.authLinks}>
        <p>
          Remembered your password? <Link to="/login">Login</Link>
        </p>
        <p>
          Don't have an account? <Link to="/register">Register</Link>
        </p>
      </div>
    </div>
  );
};

export default ForgotPassword;
