import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/css/ExpenseManagement.css";

const ExpenseManagement = () => {
  const [expenses, setExpenses] = useState([]);
  const [newCategory, setNewCategory] = useState("");
  const [newAmount, setNewAmount] = useState("");
  const [message, setMessage] = useState("");
  const [currentMonth, setCurrentMonth] = useState("");
  const [budgetAllocated, setBudgetAllocated] = useState(0);

  const categories = [
    "Groceries",
    "Rent",
    "Utilities",
    "Entertainment",
    "Savings",
    "Miscellaneous",
  ];

  useEffect(() => {
    const month = new Date().toLocaleString("default", { month: "long" });
    const year = new Date().getFullYear();
    setCurrentMonth(`${month} ${year}`);
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem("token");

      if (!token) {
        setMessage("You must be logged in to view this data.");
        return;
      }

      try {
        // Fetch expenses data
        const { data: expensesData } = await axios.get("http://localhost:3001/api/expense", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setExpenses(expensesData);

        // Fetch categories and budget data
        const { data: budgetData } = await axios.get("http://localhost:3001/api/budget", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setNewCategory(budgetData.categories);
        setBudgetAllocated(budgetData.budgetAllocated);
      } catch (err) {
        setMessage(err.response?.status === 401 ? "Unauthorized. Please login again." : "Error fetching data.");
      }
    };

    fetchData();
  }, []);

  const handleAddExpense = async () => {
    if (!newCategory || !newAmount || newAmount <= 0) {
      setMessage("Please provide a valid category and amount.");
      return;
    }

    const totalExpensesInCategory = expenses
      .filter((expense) => expense.category === newCategory)
      .reduce((sum, expense) => sum + expense.expense, 0);

    if (totalExpensesInCategory + parseFloat(newAmount) > budgetAllocated) {
      setMessage("Expenses for this category exceed the allocated budget.");
      return;
    }

    const token = localStorage.getItem("token");

    if (!token) {
      setMessage("You must be logged in to add an expense.");
      return;
    }

    try {
      const { data: newExpense } = await axios.post(
        "http://localhost:3001/api/expense",
        { category: newCategory, expense: parseFloat(newAmount) },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setExpenses((prevExpenses) => [...prevExpenses, newExpense]);
      setMessage("Expense added successfully!");
      setNewCategory("");
      setNewAmount("");
    } catch (err) {
      setMessage(err.response?.status === 401 ? "Unauthorized. Please login again." : "Error adding expense.");
    }
  };

  const handleExpenseUpdate = async (id, updatedAmount) => {
    if (updatedAmount < 0) {
      setMessage("Expense amount cannot be negative.");
      return;
    }

    const totalExpensesInCategory = expenses
      .filter((expense) => expense.category === newCategory)
      .reduce((sum, expense) => sum + expense.expense, 0);

    if (totalExpensesInCategory + parseFloat(updatedAmount) > budgetAllocated) {
      setMessage("Updated expense exceeds the allocated budget.");
      return;
    }

    const token = localStorage.getItem("token");

    if (!token) {
      setMessage("You must be logged in to update an expense.");
      return;
    }

    try {
      await axios.put(
        `http://localhost:3001/api/expense/${id}`,
        { expense: updatedAmount },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const updatedExpenses = expenses.map((expense) =>
        expense._id === id ? { ...expense, expense: updatedAmount } : expense
      );
      setExpenses(updatedExpenses);
      setMessage("Expense updated successfully!");
    } catch (err) {
      setMessage(err.response?.status === 401 ? "Unauthorized. Please login again." : "Error updating expense.");
    }
  };

  return (
    <div className="expense-management-container">
      <h2>Expense Management for {currentMonth}</h2>

      {message && <p>{message}</p>}

      <div className="form-container">
        <select
          className="expense-category-select"
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
        >
          <option value="">Select Category</option>
          {categories.map((category, index) => (
            <option key={index} value={category}>
              {category}
            </option>
          ))}
        </select>

        <input
          type="number"
          value={newAmount}
          onChange={(e) => setNewAmount(e.target.value)}
          placeholder="Amount"
        />

        <button onClick={handleAddExpense}>Add Expense</button>
      </div>

      <h3>Current Expenses</h3>
      <ul>
        {expenses.map((expense) => (
          <li key={expense._id}>
            <span>{expense.category}: ₹{expense.expense}</span>
            <button onClick={() => handleExpenseUpdate(expense._id, prompt("New amount:"))}>
              Update
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ExpenseManagement;
