import React, { useState } from "react";
import axios from "axios";
import styles from "../assets/css/Auth.module.css";
import title  from '../assets/images/logo.png'

const ForgetPassword = () => {
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!email) {
      setMessage("Please enter a valid email address.");
      return;
    }
    try {
      const res = await axios.post("http://localhost:3001/api/auth/forgot-password", { email });
      setMessage(res.data.message || "Password reset instructions sent!");
    } catch (err) {
      setMessage(err.response?.data?.message || "Failed to send instructions.");
    }
  };

  return (
    <div className={styles.authContainer}>
      <img src={title} alt="Title Logo" className={styles.titlelogo}/>
      <h2>Forgot Password</h2>
      <form onSubmit={handleSubmit} className={styles.authForm}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Enter your email"
          required
          className={styles.authInput}
        />
        <button type="submit" className={styles.authButton}>Submit</button>
      </form>
      {message && <p className={styles.message}>{message}</p>}
    </div>
  );
};

export default ForgetPassword;
