import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/css/ExpenseManagement.css";

const ExpenseManagement = () => {
  const [categories, setCategories] = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [expenseAmount, setExpenseAmount] = useState("");
  const [expenseDate, setExpenseDate] = useState("");
  const [message, setMessage] = useState("");
  const [totalExpenses, setTotalExpenses] = useState(0);

  useEffect(() => {
    // Fetch categories and expenses
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");

        // Fetch categories
        const categoriesResponse = await axios.get("http://localhost:3001/api/categories", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setCategories(categoriesResponse.data);

        // Fetch expenses
        const expensesResponse = await axios.get("http://localhost:3001/api/expenses", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setExpenses(expensesResponse.data);
      } catch (err) {
        setMessage("Error fetching data");
      }
    };

    fetchData();
  }, []);

  // Handle Add Expense
  const handleAddExpense = async () => {
    if (!selectedCategory || !expenseAmount || !expenseDate) {
      setMessage("Please provide valid category, amount, and date.");
      return;
    }

    // Check if the expense exceeds the allocated budget
    const category = categories.find((cat) => cat.name === selectedCategory);
    const currentCategoryExpenses = expenses.filter(
      (expense) => expense.category === selectedCategory
    );
    const totalCategoryExpenses = currentCategoryExpenses.reduce((sum, exp) => sum + exp.amount, 0);

    if (totalCategoryExpenses + parseFloat(expenseAmount) > category.budget) {
      setMessage("Expense exceeds the allocated budget for this category.");
      return;
    }

    const currentDate = new Date().toISOString().split("T")[0];

    try {
      const token = localStorage.getItem("token");

      const response = await axios.post(
        "http://localhost:3001/api/expenses",
        {
          category: selectedCategory,
          amount: parseFloat(expenseAmount),
          date: expenseDate,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setExpenses((prevExpenses) => [...prevExpenses, response.data]);
      setMessage("Expense added successfully!");

      // Reset input fields
      setExpenseAmount("");
      setExpenseDate("");

      setTimeout(() => setMessage(""), 3000);
    } catch (err) {
      setMessage("Error adding expense");
    }
  };

  // Handle Update Expense
  const handleUpdateExpense = async (id, updatedAmount) => {
    const updatedAmountFloat = parseFloat(updatedAmount);
    if (isNaN(updatedAmountFloat) || updatedAmountFloat <= 0) {
      setMessage("Please provide a valid expense amount.");
      return;
    }

    // Check if the updated expense exceeds the allocated budget
    const category = categories.find((cat) => cat.name === selectedCategory);
    const currentCategoryExpenses = expenses.filter(
      (expense) => expense.category === selectedCategory
    );
    const totalCategoryExpenses = currentCategoryExpenses.reduce((sum, exp) => sum + exp.amount, 0);

    if (totalCategoryExpenses - expenses.find(exp => exp._id === id).amount + updatedAmountFloat > category.budget) {
      setMessage("Updated expense exceeds the allocated budget for this category.");
      return;
    }

    try {
      const token = localStorage.getItem("token");

      await axios.put(
        `http://localhost:3001/api/expenses/${id}`,
        { amount: updatedAmountFloat, date: expenseDate },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Update expenses in the state
      setExpenses((prevExpenses) =>
        prevExpenses.map((expense) =>
          expense._id === id ? { ...expense, amount: updatedAmountFloat, date: expenseDate } : expense
        )
      );

      setMessage("Expense updated successfully!");
      setTimeout(() => setMessage(""), 3000);
    } catch (err) {
      setMessage("Error updating expense");
    }
  };

  // Handle Remove Expense
  const handleRemoveExpense = async (id) => {
    try {
      const token = localStorage.getItem("token");

      await axios.delete(`http://localhost:3001/api/expenses/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setExpenses((prevExpenses) => prevExpenses.filter((expense) => expense._id !== id));

      setMessage("Expense removed successfully!");
      setTimeout(() => setMessage(""), 3000);
    } catch (err) {
      setMessage("Error removing expense");
    }
  };

  return (
    <div className="expense-container">
      <h2>Expense Management</h2>

      {message && <p className="message">{message}</p>}

      <div className="add-expense">
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          <option value="">Select Category</option>
          {categories.map((category) => (
            <option key={category._id} value={category.name}>
              {category.name}
            </option>
          ))}
        </select>

        <input
          type="number"
          placeholder="Expense Amount"
          value={expenseAmount}
          onChange={(e) => setExpenseAmount(e.target.value)}
        />
        <input
          type="date"
          value={expenseDate}
          onChange={(e) => setExpenseDate(e.target.value)}
        />
        <button onClick={handleAddExpense}>Add Expense</button>
      </div>

      <div className="expense-list">
        {expenses.map((expense) => (
          <div key={expense._id} className="expense-item">
            <span>{expense.category}: â‚¹{expense.amount}</span>
            <span>Date: {new Date(expense.date).toLocaleDateString()}</span>
            <input
              type="number"
              value={expense.amount}
              onChange={(e) => handleUpdateExpense(expense._id, e.target.value)}
            />
            <button onClick={() => handleRemoveExpense(expense._id)}>Remove</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ExpenseManagement;
