// src/components/ExpenseTracking.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import styles from "../assets/css/ExpenseTracking.module.css";

const ExpenseTracking = () => {
  const [expenses, setExpenses] = useState([]);
  const [newExpense, setNewExpense] = useState({ description: "", amount: 0 });

  useEffect(() => {
    fetchExpenses();
  }, []);

  const fetchExpenses = async () => {
    try {
      const response = await axios.get("http://localhost:3001/api/expenses");
      setExpenses(response.data);
    } catch (error) {
      console.error("Error fetching expenses:", error.message);
    }
  };

  const handleAddExpense = async () => {
    try {
      await axios.post("http://localhost:3001/api/expenses", newExpense);
      fetchExpenses();
      setNewExpense({ description: "", amount: 0 });
    } catch (error) {
      console.error("Error adding expense:", error.message);
    }
  };

  return (
    <div className={styles.container}>
      <h2>Expense Tracking</h2>
      <ul>
        {expenses.map((expense) => (
          <li key={expense._id}>
            {expense.description} - â‚¹{expense.amount}
          </li>
        ))}
      </ul>
      <input
        type="text"
        placeholder="Description"
        value={newExpense.description}
        onChange={(e) => setNewExpense({ ...newExpense, description: e.target.value })}
      />
      <input
        type="number"
        placeholder="Amount"
        value={newExpense.amount}
        onChange={(e) => setNewExpense({ ...newExpense, amount: parseFloat(e.target.value) })}
      />
      <button onClick={handleAddExpense}>Add Expense</button>
    </div>
  );
};

export default ExpenseTracking;
