import React, { useState, useEffect } from "react";
import axios from "axios";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from "chart.js";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const Reports = () => {
  const [reportData, setReportData] = useState({
    totalIncome: 0,
    budgetAllocated: 0,
    totalExpense: 0,
    incomeGraphs: { labels: [], datasets: [{ label: "Income", data: [] }] },
    budgetGraphs: { labels: [], datasets: [{ label: "Allocated", data: [] }, { label: "Remaining", data: [] }] },
    expenseGraphs: { labels: [], datasets: [{ label: "Expense", data: [] }] }
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch report data when component mounts
  useEffect(() => {
    const fetchReportData = async () => {
      setLoading(true);
      setError(null);
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("http://localhost:3001/api/reports", {
          headers: { Authorization: `Bearer ${token}` }
        });

        const { totalIncome, budgetAllocated, totalExpense, incomeGraphs, budgetGraphs, expenseGraphs } = res.data;
        setReportData({
          totalIncome,
          budgetAllocated,
          totalExpense,
          incomeGraphs,
          budgetGraphs,
          expenseGraphs
        });
      } catch (err) {
        setError("Error fetching report data. Please try again later.");
        console.error("Error fetching report data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchReportData();
  }, []);

  // Graph options configuration
  const graphOptions = {
    responsive: true,
    plugins: { title: { display: true, text: "" } },
    scales: { x: { beginAtZero: true }, y: { beginAtZero: true } }
  };

  // Handle loading and error states
  if (loading) return <div>Loading report data...</div>;
  if (error) return <div>{error}</div>;

  // Dynamically render a Line chart with provided data and options
  const renderGraph = (data, title) => {
    if (data.labels.length === 0) return <div>No data available</div>;
    return <Line data={data} options={{ ...graphOptions, plugins: { title: { text: title } } }} />;
  };

  return (
    <div className="report-container">
      <div className="report-summary">
        <div className="summary-box">
          <h3>Total Income</h3>
          <p>{reportData.totalIncome}</p>
        </div>
        <div className="summary-box">
          <h3>Budget Allocated</h3>
          <p>{reportData.budgetAllocated}</p>
        </div>
        <div className="summary-box">
          <h3>Budget Remaining</h3>
          <p>{reportData.totalIncome - reportData.budgetAllocated}</p>
        </div>
        <div className="summary-box">
          <h3>Total Expense</h3>
          <p>{reportData.totalExpense}</p>
        </div>
        <div className="summary-box">
          <h3>Amount Left to Spend</h3>
          <p>{reportData.totalIncome - reportData.totalExpense}</p>
        </div>
      </div>

      <div className="graphs-container">
        <div className="graph-box">
          {renderGraph(reportData.incomeGraphs, "Income by Source")}
        </div>
        <div className="graph-box">
          {renderGraph(reportData.incomeGraphs, "Income by Date")}
        </div>
      </div>

      <div className="graphs-container">
        <div className="graph-box">
          {renderGraph(reportData.budgetGraphs, "Budget Allocation by Category")}
        </div>
        <div className="graph-box">
          {renderGraph(reportData.budgetGraphs, "Budget Remaining by Category")}
        </div>
      </div>

      <div className="graphs-container">
        <div className="graph-box">
          {renderGraph(reportData.expenseGraphs, "Expense by Category")}
        </div>
        <div className="graph-box">
          {renderGraph(reportData.expenseGraphs, "Expense by Date")}
        </div>
      </div>
    </div>
  );
};

export default Reports;
