import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/css/BudgetAllocation.css";

const BudgetAllocation = () => {
  const [categories, setCategories] = useState([]);
  const [totalIncome, setTotalIncome] = useState(0);
  const [budgetAllocated, setBudgetAllocated] = useState(0);
  const [remainingBudget, setRemainingBudget] = useState(0);
  const [newCategory, setNewCategory] = useState("");
  const [newBudget, setNewBudget] = useState("");
  const [message, setMessage] = useState("");
  const [currentMonth, setCurrentMonth] = useState("");

  const category_data = [
    "Food", "Movies", "OTT", "Shopping", "Travel", "Rent", 
    "Utilities", "Health", "Insurance", "Investments", 
    "Savings", "Miscellaneous"
  ];

  useEffect(() => {
    const month = new Date().toLocaleString("default", { month: "long" });
    const year = new Date().getFullYear();
    setCurrentMonth(`${month} ${year}`);
  }, []);

  // Fetch budget and income data from backend
  useEffect(() => {
    const fetchBudgetData = async () => {
      try {
        const token = localStorage.getItem("token");

        // Fetch budget data
        const budgetResponse = await axios.get("http://localhost:3001/api/budget", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const { budgetAllocated, categories } = budgetResponse.data;

        // Fetch income data
        const incomeResponse = await axios.get("http://localhost:3001/api/income", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const totalIncomeFromAPI = incomeResponse.data.income.reduce((sum, income) => sum + income.amount, 0);

        setTotalIncome(totalIncomeFromAPI);
        setBudgetAllocated(budgetAllocated);
        setRemainingBudget(totalIncomeFromAPI - budgetAllocated);
        setCategories(categories);
      } catch (err) {
        setMessage("Error fetching data");
      }
    };

    fetchBudgetData();
  }, []);

  // Add a new category
  const handleAddCategory = async () => {
    if (!newCategory || !newBudget || newBudget <= 0) {
      setMessage("Please provide a valid category and budget.");
      return;
    }

    const newTotalAllocated = budgetAllocated + parseFloat(newBudget);
    if (newTotalAllocated > totalIncome) {
      setMessage("Budget allocated exceeds total income. Please adjust.");
      return;
    }

    const currentDate = new Date().toISOString().split("T")[0]; // "2024-11-09" format

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:3001/api/budget",
        { category: newCategory, budget: parseFloat(newBudget), date: currentDate },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const addedCategory = response.data;
      setCategories((prevCategories) => [...prevCategories, addedCategory]);
      setBudgetAllocated(newTotalAllocated);
      setRemainingBudget(totalIncome - newTotalAllocated);
      setMessage("Category added successfully!");
      setNewCategory("");
      setNewBudget("");

      setTimeout(() => setMessage(""), 3000); // Clear the message after 3 seconds
    } catch (err) {
      setMessage("Error adding category");
    }
  };

  // Update a category budget
  const handleBudgetUpdate = async (id, updatedBudget) => {
    const updatedBudgetFloat = parseFloat(updatedBudget);

    if (isNaN(updatedBudgetFloat) || updatedBudgetFloat <= 0) {
      setMessage("Please provide a valid budget amount.");
      return;
    }

    const newTotalAllocated = categories.reduce((sum, cat) => {
      return sum + (cat._id === id ? updatedBudgetFloat : cat.budget);
    }, 0);

    if (newTotalAllocated > totalIncome) {
      setMessage("Budget allocation exceeds total income. Please adjust.");
      return;
    }

    const currentDate = new Date().toLocaleDateString();

    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `http://localhost:3001/api/budget/${id}`,
        { budget: updatedBudgetFloat, date: currentDate },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setCategories((prevCategories) =>
        prevCategories.map((item) =>
          item._id === id ? { ...item, budget: updatedBudgetFloat, date: currentDate } : item
        )
      );

      setBudgetAllocated(newTotalAllocated);
      setRemainingBudget(totalIncome - newTotalAllocated);
      setMessage("Budget updated successfully!");

      setTimeout(() => setMessage(""), 3000); // Clear the message after 3 seconds
    } catch (err) {
      setMessage("Error updating budget");
    }
  };

  // Remove a category
  const handleRemoveCategory = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:3001/api/budget/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const updatedCategories = categories.filter((item) => item._id !== id);
      setCategories(updatedCategories);

      const updatedBudgetAllocated = updatedCategories.reduce((sum, cat) => sum + cat.budget, 0);
      setBudgetAllocated(updatedBudgetAllocated);
      setRemainingBudget(totalIncome - updatedBudgetAllocated);

      setMessage("Category removed successfully!");

      setTimeout(() => setMessage(""), 3000); // Clear the message after 3 seconds
    } catch (err) {
      setMessage("Error removing category");
    }
  };

  return (
    <div className="budget-container">
      <h2>Budget Allocation</h2>
      <p className="date-display">Date: {currentMonth}</p>

      <div className="budget-summary">
        <h3>Total Income: ₹{totalIncome}</h3>
        <h4>Budget Allocated: ₹{budgetAllocated}</h4>
        <h4>Remaining Budget: ₹{remainingBudget}</h4>
      </div>

      {message && <p className="message">{message}</p>}

      <div className="add-category">
        <select
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
        >
          <option value="">Select Category</option>
          {category_data.map((category, index) => (
            <option key={index} value={category}>
              {category}
            </option>
          ))}
        </select>
        <input
          type="number"
          placeholder="Budget"
          value={newBudget}
          onChange={(e) => setNewBudget(e.target.value)}
        />
        <button onClick={handleAddCategory}>Add Category</button>
      </div>

      <div className="category-list">
        {categories.map((category) => (
          <div key={category._id} className="category-item">
            <span>{category.category}: ₹{category.budget}</span>
            <span>Date: {new Date(category.date).toLocaleDateString()}</span>
            <input
              type="number"
              value={category.budget}
              onChange={(e) => handleBudgetUpdate(category._id, e.target.value)}
            />
            <button onClick={() => handleRemoveCategory(category._id)}>Remove</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BudgetAllocation;
