import React, { useEffect } from 'react';
import Logo from '../assets/logo.png';
import './css/Navbar.css';
import { Link as ScrollLink } from 'react-scroll';
import { useNavigate } from 'react-router-dom';


function Navbar() {
    const navigate = useNavigate();
    useEffect(() => {
        const handleScroll = () => {
            const header = document.querySelector(".App-header");
            if (header) {
                header.style.backgroundColor = window.scrollY > 0 ? "rgba(50, 50, 55, 0.5)" : "transparent";
                header.style.backdropFilter = window.scrollY > 0 ? "saturate(180%) blur(10px)" : "none";
                header.style.WebkitBackdropFilter = window.scrollY > 0 ? "blur(10px)" : "none";
            }
        };

        window.addEventListener("scroll", handleScroll);

        return () => {
            window.removeEventListener("scroll", handleScroll);
        };
    }, []);

    return (
        <div className="App-header">
            <div className="App-logo">
                <Link to="App-HomePage" smooth={true} duration={500}>
                    <img onClick={() => navigate('/')} src={Logo} alt="Logo" width={200} />
                </Link>
            </div>
            <div className="App-menu">
                <ul />
                    <Link to="App-about" smooth={true} duration={500} offset={-150}><li onClick={() => navigate('/')}>About</li></Link>
                    <ScrollLink to="App-guidelines" smooth={true} duration={500} offset={-150}><li onClick={() => navigate('/')}>Guidelines</li></ScrollLink>
                    <ScrollLink to="App-domains" smooth={true} duration={500} offset={-150}><li onClick={() => navigate('/')}>Domains</li></ScrollLink>
                    <ScrollLink to="App-contact" smooth={true} duration={500} offset={-150}><li onClick={() => navigate('/')}>Contact</li></ScrollLink>
            </div>
            <div className="App-Register-Button">
                <button onClick={() => navigate('/register')} class="animated-button">
                <svg xmlns="http://www.w3.org/2000/svg" class="arr-2" viewBox="0 0 24 24">
                    <path
                    d="M16.1716 10.9999L10.8076 5.63589L12.2218 4.22168L20 11.9999L12.2218 19.778L10.8076 18.3638L16.1716 12.9999H4V10.9999H16.1716Z"
                    ></path>
                </svg>
                <span class="text">R E G I S T E R</span>
                <span class="circle"></span>
                <svg xmlns="http://www.w3.org/2000/svg" class="arr-1" viewBox="0 0 24 24">
                    <path
                    d="M16.1716 10.9999L10.8076 5.63589L12.2218 4.22168L20 11.9999L12.2218 19.778L10.8076 18.3638L16.1716 12.9999H4V10.9999H16.1716Z"
                    ></path>
                </svg>
                </button>
            </div>
        </div>
    );
}

export default Navbar;
