import React, { useState, useEffect } from "react";
import axios from "axios";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const Reports = () => {
  const [totalIncome, setTotalIncome] = useState(0);
  const [budgetAllocated, setBudgetAllocated] = useState(0);
  const [totalExpense, setTotalExpense] = useState(0);
  const [incomeGraphsData, setIncomeGraphsData] = useState({
    labels: [],
    datasets: [{
      label: 'Income',
      data: []
    }]
  });
  const [budgetGraphsData, setBudgetGraphsData] = useState({
    labels: [],
    datasets: [
      { label: 'Allocated', data: [] }, 
      { label: 'Remaining', data: [] }
    ]
  });
  const [expenseGraphsData, setExpenseGraphsData] = useState({
    labels: [],
    datasets: [{
      label: 'Expense',
      data: []
    }]
  });

  useEffect(() => {
    const fetchReportData = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("http://localhost:3001/api/reports", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = res.data;
        setTotalIncome(data.totalIncome);
        setBudgetAllocated(data.budgetAllocated);
        setTotalExpense(data.totalExpense);

        // Only update the graph data if it is available
        if (data.incomeGraphs) setIncomeGraphsData(data.incomeGraphs);
        if (data.budgetGraphs) setBudgetGraphsData(data.budgetGraphs);
        if (data.expenseGraphs) setExpenseGraphsData(data.expenseGraphs);

      } catch (err) {
        console.error("Error fetching report data:", err);
      }
    };

    fetchReportData();
  }, []);

  const incomeGraphOptions = {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: 'Income by Source',
      },
    },
    scales: {
      x: {
        beginAtZero: true,
      },
      y: {
        beginAtZero: true,
      },
    },
  };

  const expenseGraphOptions = {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: 'Expense by Category',
      },
    },
    scales: {
      x: {
        beginAtZero: true,
      },
      y: {
        beginAtZero: true,
      },
    },
  };

  const budgetGraphOptions = {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: 'Budget Allocation and Remaining',
      },
    },
    scales: {
      x: {
        beginAtZero: true,
      },
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div>
      <div className="report-summary">
        <div>Total Income: {totalIncome}</div>
        <div>Budget Allocated: {budgetAllocated}</div>
        <div>Budget Remaining: {totalIncome - budgetAllocated}</div>
        <div>Total Expense: {totalExpense}</div>
        <div>Amount Left to Spend: {totalIncome - totalExpense}</div>
      </div>

      <div className="graphs-container">
        <div className="graph-box">
          <Line data={incomeGraphsData} options={incomeGraphOptions} />
        </div>

        <div className="graph-box">
          <Line data={budgetGraphsData} options={budgetGraphOptions} />
        </div>

        <div className="graph-box">
          <Line data={expenseGraphsData} options={expenseGraphOptions} />
        </div>
      </div>
    </div>
  );
};

export default Reports;
