import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/css/BudgetAllocation.css";

const BudgetAllocation = () => {
  const [categories, setCategories] = useState([]);
  const [totalIncome, setTotalIncome] = useState(null);
  const [budgetAllocated, setBudgetAllocated] = useState(0);
  const [remainingBudget, setRemainingBudget] = useState(0);
  const [newCategory, setNewCategory] = useState("");
  const [newBudget, setNewBudget] = useState("");
  const [message, setMessage] = useState("");
  const [currentMonth, setCurrentMonth] = useState("");
  const [isLoading, setIsLoading] = useState(true);

  const category_data = [
    "Food", "Movies", "OTT", "Shopping", "Travel", "Rent",
    "Utilities", "Health", "Insurance", "Investments",
    "Savings", "Miscellaneous"
  ];

  // Set current month and year on initial load
  useEffect(() => {
    const month = new Date().toLocaleString("default", { month: "long" });
    const year = new Date().getFullYear();
    setCurrentMonth(`${month} ${year}`);
  }, []);

  // Fetch budget and income data from the backend
  useEffect(() => {
    const fetchBudgetData = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:3001/api/budget", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const { totalIncome, categories, totalBudget, totalRemainingBudget } = response.data;

        if (typeof totalIncome === "number" && !isNaN(totalIncome)) {
          setTotalIncome(totalIncome);
        } else {
          setMessage("Invalid total income data.");
        }

        if (typeof totalRemainingBudget === "number" && !isNaN(totalRemainingBudget)) {
          setRemainingBudget(totalRemainingBudget);
        } else {
          setMessage("Invalid total remaining budget data.");
        }

        if (typeof totalBudget === "number" && !isNaN(totalBudget)) {
          setBudgetAllocated(totalBudget);
        } else {
          setMessage("Invalid total allocated budget data.");
        }

        setCategories(categories);
        setIsLoading(false);
      } catch (err) {
        setMessage("Error fetching data.");
        setIsLoading(false);
      }
    };

    fetchBudgetData();
  }, []);

  // Add a new category
  const handleAddCategory = async () => {
    if (!newCategory || !newBudget || newBudget <= 0) {
      return setMessage("Please provide a valid category and budget.");
    }

    const newTotalAllocated = budgetAllocated + parseFloat(newBudget);
    if (newTotalAllocated > totalIncome) {
      return setMessage("Budget allocated exceeds total income. Please adjust.");
    }

    const currentDate = new Date().toISOString().split("T")[0];

    try {
      const token = localStorage.getItem("token");

      // Check if the category with the same date already exists
      const existingCategoryIndex = categories.findIndex(
        (category) => category.category === newCategory && category.date === currentDate
      );

      if (existingCategoryIndex !== -1) {
        // Category exists, so add to the existing one
        const updatedCategory = { ...categories[existingCategoryIndex] };
        updatedCategory.budgetAllocated += parseFloat(newBudget);
        updatedCategory.remainingBudget += parseFloat(newBudget);

        // Send update request to the backend to add values
        await axios.put(
          `http://localhost:3001/api/budget/${updatedCategory._id}`,
          updatedCategory,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        setCategories((prevCategories) => {
          const updatedCategories = [...prevCategories];
          updatedCategories[existingCategoryIndex] = updatedCategory;
          return updatedCategories;
        });
      } else {
        // Category doesn't exist, create a new one
        const response = await axios.post(
          "http://localhost:3001/api/budget",
          { category: newCategory, budgetAllocated: parseFloat(newBudget), remainingBudget: parseFloat(newBudget), date: currentDate },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        setCategories((prevCategories) => [...prevCategories, response.data]);
      }

      setBudgetAllocated(newTotalAllocated);
      setRemainingBudget(totalIncome - newTotalAllocated);
      setMessage("Category added/updated successfully!");
    } catch (err) {
      setMessage("Error adding/updating category.");
    }
  };


  // Update a category's budget
  const handleBudgetUpdate = async (id, updatedBudget) => {
    const updatedBudgetFloat = parseFloat(updatedBudget);

    if (isNaN(updatedBudgetFloat) || updatedBudgetFloat <= 0) {
      return setMessage("Please provide a valid budget amount.");
    }

    const newTotalAllocated = categories.reduce((sum, cat) => {
      return sum + (cat._id === id ? updatedBudgetFloat : cat.budgetAllocated);
    }, 0);

    if (newTotalAllocated > totalIncome) {
      return setMessage("Budget allocation exceeds total income. Please adjust.");
    }

    const currentDate = new Date().toLocaleDateString();

    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `http://localhost:3001/api/budget/${id}`,
        { budgetAllocated: updatedBudgetFloat, remainingBudget: updatedBudgetFloat, date: currentDate },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setCategories(prevCategories =>
        prevCategories.map(category =>
          category._id === id
            ? { ...category, budgetAllocated: updatedBudgetFloat, remainingBudget: updatedBudgetFloat, date: currentDate }
            : category
        )
      );

      setBudgetAllocated(newTotalAllocated);
      setRemainingBudget(totalIncome - newTotalAllocated);
    } catch (err) {
      setMessage("Error updating budget.");
    }
  };

  // Remove a category
  const handleRemoveCategory = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:3001/api/budget/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setCategories(prevCategories => prevCategories.filter(category => category._id !== id));
      setMessage("Category removed successfully.");
    } catch (err) {
      setMessage("Error removing category.");
    }
  };

  return (
    <div className="budget-container">
      <h2>Budget Allocation</h2>
      <p className="date-display">Month: {currentMonth}</p>
      <div className="budget-summary">
        {isLoading ? (
          <h3>Loading Total Income...</h3>
        ) : (
          <h3>Total Income: ₹{totalIncome || 0}</h3>
        )}
        <h4>Allocated Budget: ₹{budgetAllocated}</h4>
        <h4>Remaining Budget: ₹{remainingBudget}</h4>
      </div>

      {message && <p className="message">{message}</p>}

      <p>Add Category & Budget</p>
      <div className="add-category">
        <select value={newCategory} onChange={(e) => setNewCategory(e.target.value)}>
          <option value="">Select a Category</option>
          {category_data.map((category, index) => (
            <option key={index} value={category}>{category}</option>
          ))}
        </select>
        <input
          type="number"
          value={newBudget}
          onChange={(e) => setNewBudget(e.target.value)}
          placeholder="Enter Budget Amount"
        />
        <button onClick={handleAddCategory}>Add Category</button>
      </div>

      <p>Category & Budget List</p>
      <ul className="category-list">
        {categories.map((category) => (
          <li className="category-item" key={category._id}>
            <span>{category.category}: ₹{category.budgetAllocated}</span>
            <span>Date: {new Date(category.date).toLocaleDateString()}</span>
            <p>Update Value:</p>&nbsp;
            <input
              type="number"
              value={category.budgetAllocated}
              onChange={(e) => handleBudgetUpdate(category._id, e.target.value)}
            />
            <button onClick={() => handleRemoveCategory(category._id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BudgetAllocation;
