import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './BudgetAllocation.css';

const BudgetAllocation = () => {
  const [budget, setBudget] = useState([]);
  const [newCategory, setNewCategory] = useState('');
  const [newAmount, setNewAmount] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchBudget();
  }, []);

  const fetchBudget = async () => {
    try {
      const response = await axios.get('/api/budget');
      setBudget(response.data);
    } catch (error) {
      setMessage('Error fetching budget data');
    }
  };

  const addBudget = async () => {
    try {
      await axios.post('/api/budget', { category: newCategory, amount: newAmount });
      fetchBudget();
      setMessage('Budget category added successfully');
      setNewCategory('');
      setNewAmount('');
    } catch (error) {
      setMessage('Failed to add budget category');
    }
  };

  const updateBudget = async (id, newAmount) => {
    try {
      await axios.put(`/api/budget/${id}`, { amount: newAmount });
      fetchBudget();
      setMessage('Budget category updated successfully');
    } catch (error) {
      setMessage('Failed to update budget category');
    }
  };

  const deleteBudget = async (id) => {
    try {
      await axios.delete(`/api/budget/${id}`);
      fetchBudget();
      setMessage('Budget category deleted successfully');
    } catch (error) {
      setMessage('Failed to delete budget category');
    }
  };

  return (
    <div className="budget-container">
      <h2>Budget Allocation</h2>

      {message && <p className="message">{message}</p>}

      <div className="add-budget">
        <input
          type="text"
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
          placeholder="Category"
        />
        <input
          type="number"
          value={newAmount}
          onChange={(e) => setNewAmount(e.target.value)}
          placeholder="Amount"
        />
        <button onClick={addBudget}>Add Budget</button>
      </div>

      <ul className="budget-list">
        {budget.map((item) => (
          <li key={item._id} className="budget-item">
            <span>{item.category}: ${item.amount}</span>
            <input
              type="number"
              defaultValue={item.amount}
              onBlur={(e) => updateBudget(item._id, e.target.value)}
            />
            <button onClick={() => deleteBudget(item._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BudgetAllocation;
