import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/css/Reports.css";

const Reports = () => {
  const [reportData, setReportData] = useState(null);
  const [message, setMessage] = useState("");

  // Fetch the report data
  useEffect(() => {
    const fetchReportData = async () => {
      const token = localStorage.getItem("token");

      if (!token) {
        setMessage("Please log in to view the report.");
        return;
      }

      try {
        const response = await axios.get("http://localhost:3001/api/report", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setReportData(response.data);
      } catch (err) {
        setMessage("Error fetching report data.");
      }
    };

    fetchReportData();
  }, []);

  if (message) {
    return <p>{message}</p>;
  }

  if (!reportData) {
    return <p>Loading...</p>;
  }

  return (
    <div className="reports-container">
      <h2>Monthly Reports</h2>
      <p>For: {new Date().toLocaleString("default", { month: "long" })} {new Date().getFullYear()}</p>

      <div className="report-summary">
        <h3>Total Income: ₹{reportData.income}</h3>
        <h3>Total Expenses: ₹{reportData.expenses}</h3>
        <h3>Total Budget Allocated: ₹{reportData.budget}</h3>
        <h3>Remaining Budget: ₹{reportData.remainingBudget}</h3>
      </div>

      <div className="report-details">
        <h4>Income Details:</h4>
        <ul>
          {reportData.incomeDetails.map((item) => (
            <li key={item._id}>
              <span>{item.source}: ₹{item.amount}</span>
              <span>Date: {new Date(item.date).toLocaleDateString()}</span>
            </li>
          ))}
        </ul>

        <h4>Expense Details:</h4>
        <ul>
          {reportData.expenseDetails.map((item) => (
            <li key={item._id}>
              <span>{item.category}: ₹{item.amount}</span>
              <span>Date: {new Date(item.date).toLocaleDateString()}</span>
            </li>
          ))}
        </ul>

        <h4>Budget Allocation Details:</h4>
        <ul>
          {reportData.budgetDetails.map((item) => (
            <li key={item._id}>
              <span>{item.category}: ₹{item.budget}</span>
              <span>Date: {new Date(item.date).toLocaleDateString()}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Reports;
