import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import "../assets/css/IncomeManagement.css";

const IncomeManagement = () => {
  const [incomeStack, setIncomeStack] = useState([]);
  const [selectedSource, setSelectedSource] = useState("");
  const [newAmount, setNewAmount] = useState("");
  const [currentMonth, setCurrentMonth] = useState("");
  const [totalIncome, setTotalIncome] = useState(0);
  const [message, setMessage] = useState("");

  const incomeSources = [
    "Pocket Money",
    "Part-Time Job",
    "Scholarship",
    "Allowance",
    "Freelancing",
    "Other",
  ];

  useEffect(() => {
    const date = new Date();
    setCurrentMonth(
      `${date.getDate()} ${date.toLocaleString("default", { month: "long" })} ${date.getFullYear()}`
    );
  }, []);

  const fetchIncome = useCallback(async () => {
    try {
      const response = await axios.get("http://localhost:3001/api/income");
      setIncomeStack(response.data);
      calculateTotal(response.data);
    } catch (error) {
      setMessage("Failed to fetch income data");
    }
  }, []);

  useEffect(() => {
    fetchIncome();
  }, [fetchIncome]);

  const calculateTotal = (incomeData) => {
    const total = incomeData.reduce((sum, item) => sum + item.amount, 0);
    setTotalIncome(total);
  };

  const addIncome = async (req, res) => {
    const { source, amount } = req.body;

    if (!source || !amount || isNaN(amount)) {
      return res.status(400).json({ message: "Invalid or missing source/amount." });
    }

    // Get userId from token or session
    const token = req.headers.authorization?.split(" ")[1];
    if (!token) {
      return res.status(401).json({ message: "Authorization token is missing." });
    }

    try {
      const decoded = jwt.verify(token, "your_jwt_secret_key");  // Replace with your actual secret
      const userId = decoded.userId;

      // Create new income entry
      const newIncome = new Income({ source, amount, userId });
      await newIncome.save();
      res.status(201).json(newIncome);  // Send back the added income
    } catch (error) {
      console.error("Error adding income:", error);
      res.status(500).json({ message: "Failed to add income.", error: error.message });
    }
  };



  const updateIncome = async (id, updatedAmount) => {
    try {
      const response = await axios.put(`http://localhost:3001/api/income/${id}`, { amount: updatedAmount });
      const updatedIncomeStack = incomeStack.map((item) =>
        item._id === id ? response.data : item
      );
      setIncomeStack(updatedIncomeStack);
      calculateTotal(updatedIncomeStack);
      setMessage("Income updated successfully!");
    } catch (error) {
      setMessage("Failed to update income.");
    }
  };

  const deleteIncome = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/income/${id}`);
      const updatedIncomeStack = incomeStack.filter((item) => item._id !== id);
      setIncomeStack(updatedIncomeStack);
      calculateTotal(updatedIncomeStack);
      setMessage("Income removed successfully!");
    } catch (error) {
      setMessage("Failed to remove income.");
    }
  };

  return (
    <div className="income-container">
      <h2>Income Management</h2>
      <p className="date-display">Date: {currentMonth}</p>

      <div className="income-summary">
        <h3>Total Income: ₹{totalIncome}</h3>
      </div>

      <div className="add-income">
        <select
          value={selectedSource}
          onChange={(e) => setSelectedSource(e.target.value)}
        >
          <option value="">Select Income Source</option>
          {incomeSources.map((source) => (
            <option key={source} value={source}>
              {source}
            </option>
          ))}
        </select>
        <input
          type="number"
          value={newAmount}
          onChange={(e) => setNewAmount(e.target.value)}
          placeholder="Amount"
        />
        <button onClick={addIncome}>Add Income</button>
      </div>

      {message && <p className="message">{message}</p>}

      <ul className="income-list">
        {incomeStack.map((item) => (
          <li key={item._id} className="income-item">
            <span>{item.source}: ₹{item.amount}</span>
            <input
              type="number"
              defaultValue={item.amount}
              onBlur={(e) => updateIncome(item._id, parseFloat(e.target.value))}
            />
            <button onClick={() => deleteIncome(item._id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default IncomeManagement;
