import React, { useState, useEffect } from "react";
import axios from "axios";

const BudgetManager = () => {
  const [budgetList, setBudgetList] = useState([]);
  const [totalIncome, setTotalIncome] = useState(0);
  const [totalBudgetAllocated, setTotalBudgetAllocated] = useState(0);
  const [message, setMessage] = useState("");

  useEffect(() => {
    fetchBudgetData();
  }, []);

  const fetchBudgetData = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("Please log in to view your budget.");
        return;
      }

      const response = await axios.get("http://localhost:3001/api/budget", {
        headers: { Authorization: `Bearer ${token}` },
      });

      const budgets = response.data.budgets;
      setBudgetList(budgets);
      setTotalBudgetAllocated(
        budgets.reduce((total, budget) => total + budget.budgetAllocated, 0)
      );
    } catch (err) {
      setMessage("Error fetching budget data.");
    }
  };

  const handleUpdateBudget = async (id, newAllocation) => {
    try {
      // Validate input
      const updatedAllocation = parseFloat(newAllocation);
      if (isNaN(updatedAllocation) || updatedAllocation <= 0) {
        setMessage("Please provide a valid budget allocation.");
        return;
      }

      const currentBudget = budgetList.find((b) => b._id === id)?.budgetAllocated || 0;
      if (updatedAllocation + totalBudgetAllocated - currentBudget > totalIncome) {
        setMessage("Updated budget exceeds total income.");
        return;
      }

      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("Please log in to update a budget.");
        return;
      }

      // Optimistically update state
      const updatedBudgetList = budgetList.map((item) =>
        item._id === id ? { ...item, budgetAllocated: updatedAllocation } : item
      );
      setBudgetList(updatedBudgetList);
      setTotalBudgetAllocated(
        updatedBudgetList.reduce((total, budget) => total + budget.budgetAllocated, 0)
      );

      // Send update to the backend
      await axios.put(
        `http://localhost:3001/api/budget/${id}`,
        { budgetAllocated: updatedAllocation },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setMessage("Budget updated successfully!");
      fetchBudgetData(); // Sync state with backend after the update
    } catch (err) {
      setMessage("Error updating budget.");
    }
  };

  return (
    <div>
      <h1>Budget Manager</h1>
      <div>
        <h2>Total Income: ₹{totalIncome}</h2>
        <h3>Total Budget Allocated: ₹{totalBudgetAllocated}</h3>
      </div>
      {message && <p>{message}</p>}
      <ul>
        {budgetList.map((budget) => (
          <li key={budget._id}>
            {budget.category}: ₹{budget.budgetAllocated}
            <input
              type="number"
              defaultValue={budget.budgetAllocated}
              onBlur={(e) => handleUpdateBudget(budget._id, e.target.value)}
            />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BudgetManager;
