import React, { useState } from "react";
import axios from "axios";
import styles from "../assets/css/ChatSupport.module.css";

const ChatSupport = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const [emailStatus, setEmailStatus] = useState("");

  const handleSend = () => {
    if (!input.trim()) return;

    const newMessage = {
      sender: "user",
      text: input.trim(),
      timestamp: new Date(),
    };

    setMessages([...messages, newMessage, botReply(newMessage)]);
    setInput("");
  };

  const botReply = (userMessage) => ({
    sender: "bot",
    text: `You said: "${userMessage.text}" (This is a simulated reply.)`,
    timestamp: new Date(),
  });

  const sendEmail = async () => {
    const conversation = messages
      .map(
        (msg) =>
          `${msg.sender === "user" ? "User" : "Support"}: ${msg.text} (${msg.timestamp.toLocaleString()})`
      )
      .join("\n");

    try {
      await axios.post("http://localhost:3001/api/chat/send-email", {
        conversation,
      });
      setEmailStatus("Conversation sent successfully!");
    } catch (error) {
      setEmailStatus("Failed to send email. Please try again.");
    }

    setTimeout(() => setEmailStatus(""), 5000); // Clear the status message after 5 seconds
  };

  return (
    <div className={styles.chatSupport}>
      <div className={styles.chatToggle} onClick={() => setIsOpen(!isOpen)}>
        {isOpen ? "Close Chat" : "Chat Support"}
      </div>

      {isOpen && (
        <div className={styles.chatWindow}>
          <div className={styles.chatHeader}>Chat Support</div>
          <div className={styles.chatBody}>
            {messages.map((msg, index) => (
              <div
                key={index}
                className={`${styles.message} ${
                  msg.sender === "user" ? styles.user : styles.bot
                }`}
              >
                <p>{msg.text}</p>
                <small>{msg.timestamp.toLocaleTimeString()}</small>
              </div>
            ))}
          </div>
          <div className={styles.chatFooter}>
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type your message..."
              className={styles.chatInput}
            />
            <button onClick={handleSend} className={styles.chatButton}>
              Send
            </button>
          </div>
          <div className={styles.chatEmail}>
            <button onClick={sendEmail} className={styles.chatEmailButton}>
              Email Conversation
            </button>
            {emailStatus && <p className={styles.emailStatus}>{emailStatus}</p>}
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatSupport;
