import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/css/ExpenseManagement.css";

const ExpenseManagement = () => {
  const [expenses, setExpenses] = useState([]);
  const [newCategory, setNewCategory] = useState("");
  const [newExpense, setNewExpense] = useState(""); // Renamed from newAmount to newExpense
  const [message, setMessage] = useState("");
  const [currentMonth, setCurrentMonth] = useState("");
  const [budgetAllocated, setBudgetAllocated] = useState(0);

  const categories = [
    "Groceries",
    "Rent",
    "Utilities",
    "Entertainment",
    "Savings",
    "Miscellaneous",
  ];

  // Set current month and year
  useEffect(() => {
    const month = new Date().toLocaleString("default", { month: "long" });
    const year = new Date().getFullYear();
    setCurrentMonth(`${month} ${year}`);
  }, []);

  // Fetch expenses and budget data
  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem("token");

      if (!token) {
        setMessage("You must be logged in to view this data.");
        return;
      }

      try {
        // Fetch expenses
        const { data: expensesData } = await axios.get("http://localhost:3001/api/expense", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setExpenses(expensesData);

        // Fetch budget and categories
        const { data: budgetData } = await axios.get("http://localhost:3001/api/budget", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setBudgetAllocated(budgetData.budgetAllocated);
        setNewCategory(budgetData.categories);
      } catch (err) {
        setMessage(
          err.response?.status === 401 ? "Unauthorized. Please login again." : "Error fetching data."
        );
      }
    };

    fetchData();
  }, []);

  // Handle adding a new expense
  const handleAddExpense = async () => {
    if (!newCategory || !newExpense || newExpense <= 0) {
      setMessage("Please provide a valid category and expense.");
      return;
    }

    const totalExpensesInCategory = expenses
      .filter(expense => expense.category === newCategory)
      .reduce((sum, expense) => sum + expense.expense, 0);

    if (totalExpensesInCategory + parseFloat(newExpense) > budgetAllocated) {
      setMessage("Expenses for this category exceed the allocated budget.");
      return;
    }

    const token = localStorage.getItem("token");

    if (!token) {
      setMessage("You must be logged in to add an expense.");
      return;
    }

    try {
      const { data: newExpenseData } = await axios.post(
        "http://localhost:3001/api/expense",
        {
          category: newCategory,
          expense: parseFloat(newExpense), // Send 'expense' instead of 'amount'
          date: new Date().toISOString(),
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setMessage("Expense added successfully!");

      // Reset the form fields
      setNewExpense("");
      setNewCategory("");

      // Fetch the updated expense list
      const response = await axios.get("http://localhost:3001/api/expense", {
        headers: { Authorization: `Bearer ${token}` },
      });

      // Assuming response.data contains the full list of expenses
      setExpenses(response.data); // Update state with the new list of expenses

    } catch (err) {
      setMessage("Error adding expense. Please try again.");
      console.error("Error:", err.response?.data || err); // Log error for debugging
    }
  };


  // Handle updating an expense
  const handleExpenseUpdate = async (id, updatedExpense) => { 
    if (updatedExpense <= 0) {
      setMessage("Expense amount must be greater than zero.");
      return;
    }

    const totalExpensesInCategory = expenses
      .filter(expense => expense.category === newCategory)
      .reduce((sum, expense) => sum + expense.expense, 0);

    if (totalExpensesInCategory + parseFloat(updatedExpense) > budgetAllocated) {
      setMessage("Updated expense exceeds the allocated budget.");
      return;
    }

    const token = localStorage.getItem("token");

    if (!token) {
      setMessage("You must be logged in to update an expense.");
      return;
    }

    try {
      await axios.put(
        `http://localhost:3001/api/expense/${id}`,
        { expense: updatedExpense }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const updatedExpenses = expenses.map((expense) =>
        expense._id === id ? { ...expense, expense: updatedExpense } : expense 
      );
      setExpenses(updatedExpenses);
      setMessage("Expense updated successfully!");
    } catch (err) {
      setMessage(
        err.response?.status === 401 ? "Unauthorized. Please login again." : "Error updating expense."
      );
    }
  };

  // Handle removing an expense
  const handleRemoveExpense = async (id) => {
    const token = localStorage.getItem("token");

    if (!token) {
      setMessage("You must be logged in to remove an expense.");
      return;
    }

    try {
      await axios.delete(`http://localhost:3001/api/expense/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const updatedExpenses = expenses.filter((expense) => expense._id !== id);
      setExpenses(updatedExpenses);
      setMessage("Expense removed successfully!");
    } catch (err) {
      setMessage(
        err.response?.status === 401 ? "Unauthorized. Please login again." : "Error removing expense."
      );
    }
  };

  const totalExpenses = expenses.reduce((sum, expense) => sum + expense.expense, 0);

  return (
    <div className="expense-container">
      <h2>Expense Management</h2>
      <p className="date-display">Month: {currentMonth}</p>

      <div className="expense-summary">
        <h3>Total Expenses: ₹{totalExpenses}</h3>
      </div>

      {message && <p className="message">{message}</p>}

      <p>Add Expense</p>
      <div className="add-expense">
        <select value={newCategory} onChange={(e) => setNewCategory(e.target.value)}>
          <option value="">Select Category</option>
          {categories.map((category, index) => (
            <option key={index} value={category}>
              {category}
            </option>
          ))}
        </select>
        <input
          type="number"
          placeholder="Expense"
          value={newExpense}
          onChange={(e) => setNewExpense(e.target.value)}
        />
        <button onClick={handleAddExpense}>Add Expense</button>
      </div>

      <ul className="expense-list">
        <p>Expenses List</p>
        {expenses.map((expense) => (
          <li key={expense._id} className="expense-item">
            <span>{expense.category}: ₹{expense.expense}</span> 
            <span>Date: {new Date(expense.date).toLocaleDateString()}</span>
            <div>
              <input
                type="number"
                value={expense.expense}
                onChange={(e) => handleExpenseUpdate(expense._id, parseFloat(e.target.value))}
              />
              <button onClick={() => handleRemoveExpense(expense._id)}>Remove</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ExpenseManagement;
