import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/css/BudgetAllocation.css";

const BudgetAllocation = () => {
  const [budgetList, setBudgetList] = useState([]);
  const [totalBudget, setTotalBudget] = useState(0);
  const [newAmount, setNewAmount] = useState("");
  const [message, setMessage] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [currentMonth, setCurrentMonth] = useState("");

  // Static list of budget categories
  const budgetCategories = ["Pocket Money", "Freelancing", "Investments", "Stipends", 'Part-time Job', 'Others'];

  // Fetch the current month and format it
  useEffect(() => {
    const month = new Date().toLocaleString("default", { month: "long" });
    const year = new Date().getFullYear();
    setCurrentMonth(`${month} ${year}`);
  }, []);

  // Fetch budget data when the component loads
  useEffect(() => {
    const fetchBudgetData = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          setMessage("Please log in to see your budget data.");
          return;
        }

        const response = await axios.get("http://localhost:3001/api/budget", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setBudgetList(response.data.budget);
        setTotalBudget(response.data.totalBudget);
      } catch (err) {
        setMessage("Error fetching budget data.");
      }
    };

    fetchBudgetData();
  }, []);

  // Add a new budget allocation
  const handleAddBudget = async () => {
    if (!selectedCategory || !newAmount) {
      setMessage("Please fill out both fields.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("Please log in to add budget.");
        return;
      }

      await axios.post(
        "http://localhost:3001/api/budget",
        { category: selectedCategory, amount: newAmount },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setMessage("Budget added successfully!");
      setNewAmount("");
      setSelectedCategory("");
      const response = await axios.get("http://localhost:3001/api/budget", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setBudgetList(response.data.budget);
      setTotalBudget(response.data.totalBudget);
    } catch (err) {
      setMessage("Error adding budget.");
    }
  };

  // Handle real-time budget update
  const handleBudgetChange = async (id, newAmount) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("Please log in to update budget.");
        return;
      }

      // Optimistically update UI
      const updatedBudgetList = budgetList.map((item) =>
        item._id === id ? { ...item, amount: newAmount } : item
      );
      setBudgetList(updatedBudgetList);
      setTotalBudget(updatedBudgetList.reduce((total, budget) => total + budget.amount, 0));

      // Send the update request to the backend
      await axios.put(
        `http://localhost:3001/api/budget/${id}`,
        { amount: newAmount },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
    } catch (err) {
      setMessage("Error updating budget.");
    }
  };

  // Remove a budget allocation
  const handleRemoveBudget = async (id) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("Please log in to remove budget.");
        return;
      }

      await axios.delete(`http://localhost:3001/api/budget/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const updatedBudgetList = budgetList.filter((item) => item._id !== id);
      setBudgetList(updatedBudgetList);
      setTotalBudget(updatedBudgetList.reduce((total, budget) => total + budget.amount, 0));

      setMessage("Budget removed successfully!");
    } catch (err) {
      setMessage("Error removing budget.");
    }
  };

  return (
    <div className="budget-container">
      <h2>Budget Management</h2>
      <p className="date-display">Month: {currentMonth}</p>

      <div className="budget-summary">
        <h3>Total Budget: ₹{totalBudget}</h3>
      </div>

      {message && <p className="message">{message}</p>}

      <p>Add Budget</p>
      <div className="add-budget">
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          <option value="">Select Category</option>
          {budgetCategories.map((category, index) => (
            <option key={index} value={category}>
              {category}
            </option>
          ))}
        </select>
        <input
          type="number"
          value={newAmount}
          onChange={(e) => setNewAmount(e.target.value)}
          placeholder="Amount"
        />
        <button onClick={handleAddBudget}>Add Budget</button>
      </div>

      <p>Budget List</p>
      <ul className="budget-list">
        {budgetList.map((item) => (
          <li key={item._id} className="budget-item">
            <span>{item.category}: ₹{item.amount}</span>
            <span>Date: {new Date(item.date).toLocaleDateString()}</span>
            <p>Update Value:</p>&nbsp;
            <input
              type="number"
              value={item.amount}
              onChange={(e) => handleBudgetChange(item._id, parseFloat(e.target.value))}
            />
            <button onClick={() => handleRemoveBudget(item._id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BudgetAllocation;
