import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/css/ExpenseManagement.css";

const ExpenseManagement = () => {
  const [expenses, setExpenses] = useState([]);
  const [categories, setCategories] = useState([]); // State for categories
  const [newCategory, setNewCategory] = useState("");
  const [newAmount, setNewAmount] = useState("");
  const [message, setMessage] = useState("");
  const [currentMonth, setCurrentMonth] = useState("");
  const [budgetAllocated, setBudgetAllocated] = useState(0);

  // Get the current month
  useEffect(() => {
    const month = new Date().toLocaleString("default", { month: "long" });
    const year = new Date().getFullYear();
    setCurrentMonth(`${month} ${year}`);
  }, []);

  // Fetch expenses and budget data
  useEffect(() => {
    const fetchExpenseData = async () => {
      try {
        const token = localStorage.getItem("token");

        // Fetch expenses data
        const expenseResponse = await axios.get("http://localhost:3001/api/expense", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const expensesData = expenseResponse.data;
        setExpenses(expensesData);

        // Fetch categories from the budget allocation endpoint
        const budgetResponse = await axios.get("http://localhost:3001/api/budget", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const fetchedCategories = budgetResponse.data.categories; // Assuming categories are stored in "categories"
        setCategories(fetchedCategories);

        setBudgetAllocated(budgetResponse.data.budgetAllocated);
      } catch (err) {
        setMessage("Error fetching data");
      }
    };

    fetchExpenseData();
  }, []);

  // Handle adding a new expense
  const handleAddExpense = async () => {
    if (!newCategory || newAmount <= 0) {
      setMessage("Please provide a valid category and amount.");
      return;
    }

    const totalExpensesInCategory = expenses
      .filter(expense => expense.category === newCategory)
      .reduce((sum, expense) => sum + expense.amount, 0);

    if (totalExpensesInCategory + parseFloat(newAmount) > budgetAllocated) {
      setMessage("Expenses for this category exceed the allocated budget.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:3001/api/expense",
        {
          category: newCategory,
          amount: parseFloat(newAmount),
          date: new Date().toISOString(), // Automatically set the current date
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setExpenses((prevExpenses) => [...prevExpenses, response.data]);
      setMessage("Expense added successfully!");
      setNewCategory("");
      setNewAmount("");
    } catch (err) {
      setMessage("Error adding expense");
    }
  };

  const handleExpenseUpdate = async (id, updatedAmount) => {
    if (updatedAmount <= 0) {
      setMessage("Please enter a valid amount.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `http://localhost:3001/api/expense/${id}`,
        { amount: updatedAmount },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const updatedExpenses = expenses.map((expense) =>
        expense._id === id ? { ...expense, amount: updatedAmount } : expense
      );

      setExpenses(updatedExpenses);
      setMessage("Expense updated successfully!");
    } catch (err) {
      setMessage("Error updating expense");
    }
  };

  const handleRemoveExpense = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:3001/api/expense/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const updatedExpenses = expenses.filter((expense) => expense._id !== id);
      setExpenses(updatedExpenses);
      setMessage("Expense removed successfully!");
    } catch (err) {
      setMessage("Error removing expense");
    }
  };

  return (
    <div className="expense-container">
      <h2>Expense Management</h2>
      <p className="date-display">Date: {currentMonth}</p>

      <div className="add-expense">
        <select
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
        >
          <option value="">Select Category</option>
          {categories.map((category) => (
            <option key={category._id} value={category.category}>
              {category.category}
            </option>
          ))}
        </select>
        <input
          type="number"
          placeholder="Amount"
          value={newAmount}
          onChange={(e) => setNewAmount(e.target.value)}
        />
        <button onClick={handleAddExpense}>Add Expense</button>
      </div>

      {message && <p className="message">{message}</p>}

      <div className="expense-list">
        {expenses.map((expense) => (
          <div key={expense._id} className="expense-item">
            <span>{expense.category}: â‚¹{expense.amount}</span>
            <span>Date: {new Date(expense.date).toLocaleDateString()}</span>
            <input
              type="number"
              value={expense.amount}
              onChange={(e) =>
                handleExpenseUpdate(expense._id, e.target.value)
              }
            />
            <button onClick={() => handleRemoveExpense(expense._id)}>Remove</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ExpenseManagement;
