import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import "../assets/css/IncomeManagement.css";

const IncomeManagement = () => {
  const [date, setDate] = useState(new Date());
  const [incomeSource, setIncomeSource] = useState('');
  const [incomeAmount, setIncomeAmount] = useState('');
  const [incomeList, setIncomeList] = useState([]);
  const [currentUser, setCurrentUser] = useState(null); // To store the authenticated user ID

  useEffect(() => {
    // Fetch current user's data from auth service (assuming JWT or similar auth method)
    const fetchCurrentUser = async () => {
      try {
        const res = await axios.get('/api/auth/me');
        setCurrentUser(res.data.user);
      } catch (error) {
        console.error('Error fetching user:', error);
      }
    };
    fetchCurrentUser();
  }, []);

  useEffect(() => {
    // Fetch existing income data for the user
    const fetchIncomeData = async () => {
      if (currentUser) {
        const res = await axios.get(`/api/income/${currentUser._id}`);
        setIncomeList(res.data.income);
      }
    };
    fetchIncomeData();
  }, [currentUser]);

  const handleAddIncome = async () => {
    const incomeData = {
      userId: currentUser._id,
      date: date.toISOString(),
      source: incomeSource,
      amount: incomeAmount,
    };
    try {
      const response = await axios.post('/api/income', incomeData);
      setIncomeList([...incomeList, response.data]);
      setIncomeSource('');
      setIncomeAmount('');
    } catch (error) {
      console.error('Error adding income:', error);
    }
  };

  const handleRemoveIncome = async (incomeId) => {
    try {
      await axios.delete(`/api/income/${incomeId}`);
      setIncomeList(incomeList.filter(item => item._id !== incomeId));
    } catch (error) {
      console.error('Error removing income:', error);
    }
  };

  const handleUpdateIncome = async (incomeId, updatedAmount) => {
    try {
      const response = await axios.put(`/api/income/${incomeId}`, { amount: updatedAmount });
      setIncomeList(incomeList.map(item => item._id === incomeId ? response.data : item));
    } catch (error) {
      console.error('Error updating income:', error);
    }
  };

  return (
    <div className="income-container">
      <h2>Income Management</h2>
      <p className="date-display">Date: {currentMonth}</p>

      <div className="income-summary">
        <h3>Total Income: ₹{totalIncome}</h3>
      </div>

      <div className="add-income">
        <select
          value={selectedSource}
          onChange={(e) => setSelectedSource(e.target.value)}
        >
          <option value="">Select Income Source</option>
          {incomeSources.map((source) => (
            <option key={source} value={source}>
              {source}
            </option>
          ))}
        </select>
        <input
          type="number"
          value={newAmount}
          onChange={(e) => setNewAmount(e.target.value)}
          placeholder="Amount"
        />
        <button onClick={addIncome}>Add Income</button>
      </div>

      {message && <p className="message">{message}</p>}

      <ul className="income-list">
        {incomeStack.map((item) => (
          <li key={item._id} className="income-item">
            <span>{item.source}: ₹{item.amount}</span>
            <input
              type="number"
              defaultValue={item.amount}
              onBlur={(e) => updateIncome(item._id, parseFloat(e.target.value))}
            />
            <button onClick={() => deleteIncome(item._id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default IncomeManagement;
