import React, { useState, useEffect } from "react";
import { Line, Pie } from "react-chartjs-2";
import axios from "axios";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const Reports = () => {
  const [reports, setReports] = useState({
    monthlyIncome: [],
    budgetAllocation: [],
    expenseReport: [],
    categoryReport: [],
    remainingBudget: [],
  });
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchUserData = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem("token");

        const res = await axios.get("http://localhost:3001/api/auth/user-details", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setUserData(res.data);
      } catch (err) {
        setError(err.response?.data?.message || "Failed to fetch user details.");
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, []);

  useEffect(() => {
    const fetchReports = async () => {
      if (!userData) return;

      try {
        const incomeResponse = await axios.get("http://localhost:3001/api/reports/monthly-income", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        const budgetResponse = await axios.get("http://localhost:3001/api/reports/budget-allocation", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        const expenseResponse = await axios.get("http://localhost:3001/api/reports/expense-report", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        const categoryResponse = await axios.get("http://localhost:3001/api/reports/category-report", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        const remainingBudgetResponse = await axios.get("http://localhost:3001/api/reports/remaining-budget", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });

        setReports({
          monthlyIncome: incomeResponse.data.data || [],
          budgetAllocation: budgetResponse.data.data || [],
          expenseReport: expenseResponse.data.data || [],
          categoryReport: categoryResponse.data.data || [],
          remainingBudget: remainingBudgetResponse.data.data || [],
        });
      } catch (error) {
        console.error("Error fetching reports:", error);
      }
    };

    fetchReports();
  }, [userData]); // Fetch reports when userData is available

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Reports</h2>

      {/* Monthly Income Report */}
      {reports.monthlyIncome.length > 0 && (
        <div>
          <h3>Monthly Income Report</h3>
          <Line data={monthlyIncomeChart} />
        </div>
      )}

      {/* Category-wise Expense Report */}
      {reports.categoryReport.length > 0 && (
        <div>
          <h3>Category-wise Expense Report</h3>
          <Pie data={categoryExpenseChart} />
        </div>
      )}

      {/* Budget Allocation Report */}
      {reports.budgetAllocation.length > 0 && (
        <div>
          <h3>Budget Allocation</h3>
          <Pie data={budgetAllocationChart} />
        </div>
      )}

      {/* Remaining Budget Report */}
      {reports.remainingBudget.length > 0 && (
        <div>
          <h3>Remaining Budget</h3>
          <Pie data={remainingBudgetChart} />
        </div>
      )}
    </div>
  );
};

export default Reports;
