import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "../assets/css/Reports.css";

const Reports = () => {
  const [reportData, setReportData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchReports = async () => {
      const token = localStorage.getItem("token");

      if (!token) {
        setMessage("You must be logged in to view reports.");
        setLoading(false);
        return;
      }

      try {
        const response = await axios.get('http://localhost:3001/api/reports/monthly-report', {
          headers: { Authorization: `Bearer ${token}` },
        });

        setReportData(response.data);
        setLoading(false);
      } catch (err) {
        setMessage("Error fetching reports.");
        setLoading(false);
      }
    };

    fetchReports();
  }, []);

  // Helper function to format large numbers
  const formatNumber = (num) => new Intl.NumberFormat().format(num);

  if (loading) {
    return <div>Loading reports...</div>;
  }

  return (
    <div className="reports-container">
      <h2>Monthly Reports</h2>

      {message && <p className="message">{message}</p>}

      {reportData && (
        <div className="report-details">
          <div className="report-item">
            <h3>Monthly Income</h3>
            <p>₹{formatNumber(reportData.monthlyIncome)}</p>
          </div>

          <div className="report-item">
            <h3>Monthly Expenditure</h3>
            <p>₹{formatNumber(reportData.monthlyExpenses)}</p>
          </div>

          <div className="report-item">
            <h3>Spends on Allocated Budget</h3>
            <p>₹{formatNumber(reportData.spendsOnAllocatedBudget)}</p>
          </div>

          <div className="category-analysis">
            <h3>Category Analysis</h3>
            {reportData.categoryAnalysis.length > 0 ? (
              <ul>
                {reportData.categoryAnalysis.map((category, index) => (
                  <li key={index}>
                    {category._id}: ₹{formatNumber(category.totalAmount)}
                  </li>
                ))}
              </ul>
            ) : (
              <p>No expenses for this month yet.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Reports;
