import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/css/IncomeManagement.css";

const IncomeManagement = () => {
  const [incomeList, setIncomeList] = useState([]);
  const [totalIncome, setTotalIncome] = useState(0);
  const [newAmount, setNewAmount] = useState("");
  const [message, setMessage] = useState("");
  const [selectedSource, setSelectedSource] = useState("");
  const [currentMonth, setCurrentMonth] = useState("");

  // Static list of income sources
  const incomeSources = ["Pocket Money", "Freelancing", "Investments", "Stipends", 'Part-time Job', 'Others'];

  // Fetch the current month and format it
  useEffect(() => {
    const month = new Date().toLocaleString("default", { month: "long" });
    const year = new Date().getFullYear(); // Get the current year
    setCurrentMonth(`${month} ${year}`); // Set both month and year
  }, []);


  // Fetch income data when the component loads
  useEffect(() => {
    const fetchIncomeData = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          setMessage("Please log in to see your income data.");
          return;
        }

        const response = await axios.get("http://localhost:3001/api/income", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setIncomeList(response.data.income);
        setTotalIncome(response.data.totalIncome);
      } catch (err) {
        setMessage("Error fetching income data");
      }
    };

    fetchIncomeData();
  }, []);

  // Add a new income entry
  const handleAddIncome = async () => {
    if (!selectedSource || !newAmount || isNaN(newAmount) || newAmount <= 0) {
      setMessage("Please fill out both fields with a valid amount.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("Please log in to add income.");
        return;
      }

      const response = await axios.post(
        "http://localhost:3001/api/income",
        { source: selectedSource, amount: parseFloat(newAmount) },  // Ensure amount is a number
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMessage("Income added successfully!");
      setNewAmount("");
      setSelectedSource("");

      // Add the new income to the list locally
      setIncomeList(prevList => [...prevList, response.data]);
      setTotalIncome(prevTotalIncome => prevTotalIncome + parseFloat(newAmount));

    } catch (err) {
      setMessage("Error adding income: " + (err.response ? err.response.data.message : err.message));
      console.error(err);
    }
  };


  // Update an existing income entry
  const handleUpdateIncome = async (id, newAmount) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("Please log in to update income.");
        return;
      }

      await axios.put(
        `http://localhost:3001/api/income/${id}`,
        { amount: newAmount },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      // Re-fetch the income data after updating an entry
      const response = await axios.get("http://localhost:3001/api/income", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setIncomeList(response.data.income);
      setTotalIncome(response.data.totalIncome);
    } catch (err) {
      setMessage("Error updating income");
    }
  };

  // Remove an income entry
  const handleRemoveIncome = async (id, amount) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("Please log in to remove income.");
        return;
      }

      await axios.delete(`http://localhost:3001/api/income/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      // Update the income list and total income without refetching from backend
      setIncomeList(prevList => prevList.filter(item => item._id !== id));
      setTotalIncome(prevTotalIncome => prevTotalIncome - parseFloat(amount));

      setMessage("Income removed successfully!");
    } catch (err) {
      setMessage("Error removing income");
    }
  };

  return (
    <div className="income-container">
      <h2>Income Management</h2>
      <p className="date-display">Date: {currentMonth}</p>

      <div className="income-summary">
        <h3>Total Income: ₹{totalIncome}</h3>
      </div>

      {message && <p className="message">{message}</p>}

      <p>Add Income</p>
      <div className="add-income">
      <select
          value={selectedSource}
          onChange={(e) => setSelectedSource(e.target.value)}
        >
          <option value="">Select Income Source</option>
          {incomeSources.map((source, index) => (
            <option key={index} value={source}>
              {source}
            </option>
          ))}
        </select>
        <input
          type="number"
          value={newAmount}
          onChange={(e) => setNewAmount(e.target.value)}
          placeholder="Amount"
        />
        <button onClick={handleAddIncome}>Add Income</button>
      </div>

      <p>Income List</p>
      <ul className="income-list">
        {incomeList.map((item) => (
          <li key={item._id} className="income-item">
            <span>{item.source}: ₹{item.amount} </span>
            <span>Date: {new Date(item.date).toLocaleDateString()}</span>
            <p>Update Value:</p>&nbsp;
            <input
              type="number"
              defaultValue={item.amount}
              onBlur={(e) => handleUpdateIncome(item._id, parseFloat(e.target.value))}
            />
            <button onClick={() => handleRemoveIncome(item._id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default IncomeManagement;
