import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/css/BudgetAllocation.css";

const BudgetAllocation = () => {
  const [categories, setCategories] = useState([]);
  const [totalIncome, setTotalIncome] = useState(0);
  const [budgetAllocated, setBudgetAllocated] = useState(0);
  const [remainingBudget, setRemainingBudget] = useState(0);
  const [newCategory, setNewCategory] = useState("");
  const [newBudget, setNewBudget] = useState("");
  const [message, setMessage] = useState("");
  const [currentMonth, setCurrentMonth] = useState("");

  const category_data = [
    "Food", "Movies", "OTT", "Shopping", "Travel", "Rent", 
    "Utilities", "Health", "Insurance", "Investments", 
    "Savings", "Miscellaneous"
  ];

  useEffect(() => {
    const month = new Date().toLocaleString("default", { month: "long" });
    const year = new Date().getFullYear();
    setCurrentMonth(`${month} ${year}`);
  }, []);

  useEffect(() => {
    const fetchBudgetData = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          setMessage("Token not found. Please log in again.");
          return;
        }

        const budgetResponse = await axios.get("http://localhost:3001/api/budget", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const { totalIncome, categories } = budgetResponse.data;
        if (!totalIncome || totalIncome <= 0) {
          setMessage("Invalid total income data.");
          return;
        }

        setTotalIncome(totalIncome);  // Set state for total income
        setCategories(categories);

        // Calculate the total allocated budget and remaining budget
        const allocated = categories.reduce((sum, category) => sum + category.budgetAllocated, 0);
        setBudgetAllocated(allocated);
        setRemainingBudget(totalIncome - allocated);

      } catch (err) {
        console.error(err);
        setMessage("Error fetching data");
      }
    };

    fetchBudgetData();
  }, []);



  const handleAddCategory = async () => {
    if (!newCategory || !newBudget || newBudget <= 0) {
      setMessage("Please provide a valid category and budget.");
      return;
    }

    const newTotalAllocated = budgetAllocated + parseFloat(newBudget);
    if (newTotalAllocated > totalIncome) {
      setMessage("Budget allocated exceeds total income. Please adjust.");
      return;
    }

    const currentDate = new Date().toISOString().split("T")[0]; // "2024-11-09" format

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:3001/api/budget",
        { category: newCategory, budgetAllocated: parseFloat(newBudget), remainingBudget: parseFloat(newBudget), date: currentDate },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const addedCategory = response.data;
      setCategories((prevCategories) => [
        ...prevCategories.filter((category) => category._id !== addedCategory._id),  // Remove any old entry with same ID
        addedCategory,  // Add the new or updated category
      ]);
      setBudgetAllocated(newTotalAllocated);
      setRemainingBudget(totalIncome - newTotalAllocated);
      setMessage("Category added or updated successfully!");
      setNewCategory("");
      setNewBudget("");
    } catch (err) {
      setMessage("Error adding or updating category");
    }
  };

  // Handle updating the budget for a category
  const handleBudgetUpdate = async (id, updatedBudget) => {
    if (updatedBudget <= 0 || updatedBudget > totalIncome) {
      setMessage("Invalid updated budget value.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await axios.put(
        `http://localhost:3001/api/budget/${id}`,
        { budgetAllocated: updatedBudget, remainingBudget: updatedBudget },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const updatedCategory = response.data;
      setCategories((prevCategories) =>
        prevCategories.map((category) =>
          category._id === id ? updatedCategory : category
        )
      );
      setMessage("Budget updated successfully!");
    } catch (err) {
      setMessage("Error updating budget");
    }
  };

  // Handle removing a category
  const handleRemoveCategory = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(
        `http://localhost:3001/api/budget/${id}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setCategories((prevCategories) =>
        prevCategories.filter((category) => category._id !== id)
      );
      setMessage("Category removed successfully!");
    } catch (err) {
      setMessage("Error removing category");
    }
  };

  return (
    <div className="budget">
      <h2>Budget Allocation for {currentMonth}</h2>
      <p>Total Income: ₹{totalIncome}</p>

      {message && <p>{message}</p>}

      <div className="category-form">
        <select
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
        >
          <option value="">Select Category</option>
          {category_data.map((cat) => (
            <option key={cat} value={cat}>
              {cat}
            </option>
          ))}
        </select>
        <input
          type="number"
          value={newBudget}
          onChange={(e) => setNewBudget(e.target.value)}
          placeholder="Enter Budget"
        />
        <button onClick={handleAddCategory}>Add Category</button>
      </div>

      <h3>Allocated Budget</h3>
      <ul>
        {categories.map((category) => (
          <li key={category._id}>
            {category.category}: ₹{category.budgetAllocated} 
            <button onClick={() => handleRemoveCategory(category._id)}>Remove</button>
            <button onClick={() => handleBudgetUpdate(category._id, prompt("New Budget:", category.budgetAllocated))}>Update</button>
          </li>
        ))}
      </ul>

      <p>Remaining Budget: ₹{remainingBudget}</p>
    </div>
  );
};

export default BudgetAllocation;
