import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/css/ExpenseManagement.css";

const ExpenseManagement = () => {
  const [expenses, setExpenses] = useState([]);
  const [newCategory, setNewCategory] = useState("");
  const [newExpense, setNewExpense] = useState("");
  const [message, setMessage] = useState("");
  const [currentMonth, setCurrentMonth] = useState("");
  const [categories, setCategories] = useState([]);
  const [totalBudgetAllocated, setTotalBudgetAllocated] = useState(0);

  // Fetch the current month and year
  useEffect(() => {
    const month = new Date().toLocaleString("default", { month: "long" });
    const year = new Date().getFullYear();
    setCurrentMonth(`${month} ${year}`);
  }, []);

  // Fetch budgets and expenses on mount
  useEffect(() => {
    const fetchExpenseData = async () => {
      try {
        const token = localStorage.getItem("token");

        if (!token) {
          setMessage("You must be logged in to access expense data.");
          return;
        }

        // Fetch budget data
        const response = await axios.get("http://localhost:3001/api/budget", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const budgets = response.data.budgets;

        // Update state with fetched data
        setCategories(budgets.map((budget) => budget.category));
        setTotalBudgetAllocated(
          budgets.reduce((total, budget) => total + budget.budgetAllocated, 0)
        );
      } catch (error) {
        console.error("Error fetching budget data:", error);
        setMessage("Error fetching budget data. Please try again later.");
      }
    };

    fetchExpenseData();
  }, []);

  // Add a new expense
  const handleAddExpense = async () => {
    if (!newCategory || !newExpense || parseFloat(newExpense) <= 0) {
      setMessage("Please enter a valid category and expense amount.");
      return;
    }

    const categoryExpenses = expenses.filter(
      (expense) => expense.category === newCategory
    );
    const totalCategoryExpenses = categoryExpenses.reduce(
      (sum, expense) => sum + expense.expense,
      0
    );

    if (totalCategoryExpenses + parseFloat(newExpense) > totalBudgetAllocated) {
      setMessage("This expense exceeds the allocated budget for the category.");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      console.log(token);
      if (!token) {
        setMessage("You must be logged in to add expenses.");
        return;
      }

      await axios.post(
        "http://localhost:3001/api/expense",
        {
          category: newCategory,
          expense: parseFloat(newExpense),
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setMessage("Expense added successfully!");
      setNewCategory("");
      setNewExpense("");
      fetchUpdatedExpenses();
    } catch (err) {
      setMessage("Error adding expense. Please try again.");
    }
  };

  // Fetch updated expenses
  const fetchUpdatedExpenses = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      setMessage("You must be logged in to view expenses.");
      return;
    }

    try {
      const { data } = await axios.get("http://localhost:3001/api/expense", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setExpenses(data.expenses);
    } catch (err) {
      setMessage("Error fetching updated expenses.");
    }
  };

  // Remove an expense
  const handleRemoveExpense = async (id) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("You must be logged in to remove expenses.");
        return;
      }

      await axios.delete(`http://localhost:3001/api/expense/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setMessage("Expense removed successfully!");
      fetchUpdatedExpenses();
    } catch (err) {
      setMessage("Error removing expense. Please try again.");
    }
  };

  return (
    <div className="expense-container">
      <h2>Expense Management</h2>
      <p className="date-display">Month: {currentMonth}</p>

      {message && <p className="message">{message}</p>}

      <div className="expense-summary">
        <h3>Total Expenses: ₹{expenses.reduce((sum, e) => sum + e.expense, 0)}</h3>
        <h4>Total Budget Allocated: ₹{totalBudgetAllocated}</h4>
        <h4>Remaining Budget: ₹{totalBudgetAllocated - expenses.reduce((sum, e) => sum + e.expense, 0)}</h4>
      </div>

      <h4>Add Expense</h4>
      <div className="add-expense">
        <select
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
        >
          <option value="">Select Category</option>
          {categories.map((category, index) => (
            <option key={index} value={category}>
              {category}
            </option>
          ))}
        </select>
        <input
          type="number"
          value={newExpense}
          onChange={(e) => setNewExpense(e.target.value)}
          placeholder="Expense Amount"
        />
        <button onClick={handleAddExpense}>Add Expense</button>
      </div>

      <div>
        <h4>Expense List</h4>
        <ul className="expense-list">
          {expenses.map((expense) => (
            <li key={expense._id} className="expense-item">
              <span>
                {expense.category}: ₹{expense.expense}
              </span>
              <span>Date: {new Date(expense.date).toLocaleDateString()}</span>
              <button onClick={() => handleRemoveExpense(expense._id)}>
                Remove
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ExpenseManagement;
