import React, { useState, useEffect } from "react";
import { Line, Pie } from "react-chartjs-2";
import axios from "axios";
import jwt_decode from "jwt-decode";

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const Reports = () => {
  const [reports, setReports] = useState({
    monthlyIncome: [],
    budgetAllocation: [],
    expenseReport: [],
    categoryReport: [],
    remainingBudget: [],
  });

  useEffect(() => {
    const fetchReports = async () => {
      const token = localStorage.getItem("token");

      if (token) {
        const decodedToken = jwt_decode(token); // Decode the token
        const userId = decodedToken.userId; // Assuming 'userId' is the field in the token

        try {
          const incomeResponse = await axios.get(`http://localhost:3001/api/reports/monthly-income/${userId}`);
          const budgetResponse = await axios.get(`http://localhost:3001/api/reports/budget-allocation/${userId}`);
          const expenseResponse = await axios.get(`http://localhost:3001/api/reports/expense-report/${userId}`);
          const categoryResponse = await axios.get(`http://localhost:3001/api/reports/category-report/${userId}`);
          const remainingBudgetResponse = await axios.get(`http://localhost:3001/api/reports/remaining-budget/${userId}`);

          setReports({
            monthlyIncome: incomeResponse.data.data || [],
            budgetAllocation: budgetResponse.data.data || [],
            expenseReport: expenseResponse.data.data || [],
            categoryReport: categoryResponse.data.data || [],
            remainingBudget: remainingBudgetResponse.data.data || [],
          });
        } catch (error) {
          console.error("Error fetching reports:", error);
        }
      }
    };

    fetchReports();
  }, []);

  // Chart configurations remain the same...
  // The rest of your chart code and JSX remains unchanged.

  return (
    <div>
      <h2>Reports</h2>

      {/* Monthly Income Report */}
      {reports.monthlyIncome.length > 0 && (
        <div>
          <h3>Monthly Income Report</h3>
          <Line data={monthlyIncomeChart} />
        </div>
      )}

      {/* Category-wise Expense Report */}
      {reports.categoryReport.length > 0 && (
        <div>
          <h3>Category-wise Expense Report</h3>
          <Pie data={categoryExpenseChart} />
        </div>
      )}

      {/* Budget Allocation Report */}
      {reports.budgetAllocation.length > 0 && (
        <div>
          <h3>Budget Allocation</h3>
          <Pie data={budgetAllocationChart} />
        </div>
      )}

      {/* Remaining Budget Report */}
      {reports.remainingBudget.length > 0 && (
        <div>
          <h3>Remaining Budget</h3>
          <Pie data={remainingBudgetChart} />
        </div>
      )}
    </div>
  );
};

export default Reports;
