import React, { useState, useEffect } from "react";
import { Line, Pie } from "react-chartjs-2";
import axios from "axios";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const Reports = () => {
  const [reports, setReports] = useState({
    monthlyIncome: [],
    budgetAllocation: [],
    expenseReport: [],
    categoryReport: [],
    remainingBudget: [],
  });
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchUserData = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem("token");

        const res = await axios.get("http://localhost:3001/api/auth/user-details", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setUserData(res.data);
      } catch (err) {
        setError(err.response?.data?.message || "Failed to fetch user details.");
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, []);

  useEffect(() => {
    const fetchReports = async () => {
      if (!userData) return;

      const { userId } = userData;
      console.log("Fetching reports for user:", userId);

      try {
        const incomeResponse = await axios.get(`http://localhost:3001/api/reports/monthly-income/${userId}`);
        const budgetResponse = await axios.get(`http://localhost:3001/api/reports/budget-allocation/${userId}`);
        const expenseResponse = await axios.get(`http://localhost:3001/api/reports/expense-report/${userId}`);
        const categoryResponse = await axios.get(`http://localhost:3001/api/reports/category-report/${userId}`);
        const remainingBudgetResponse = await axios.get(`http://localhost:3001/api/reports/remaining-budget/${userId}`);

        setReports({
          monthlyIncome: incomeResponse.data.data || [],
          budgetAllocation: budgetResponse.data.data || [],
          expenseReport: expenseResponse.data.data || [],
          categoryReport: categoryResponse.data.data || [],
          remainingBudget: remainingBudgetResponse.data.data || [],
        });
      } catch (error) {
        console.error("Error fetching reports:", error);
      }
    };

    fetchReports();
  }, [userData]); // Run fetchReports when userData is available

  // Monthly Income Chart Configuration
  const monthlyIncomeChart = {
    labels: reports.monthlyIncome.map((item) => item.date),
    datasets: [
      {
        label: "Monthly Income",
        data: reports.monthlyIncome.map((item) => item.amount),
        borderColor: "rgba(75,192,192,1)",
        fill: false,
      },
    ],
  };

  // Category-wise Expense Pie Chart Configuration
  const categoryExpenseChart = {
    labels: reports.categoryReport.map((item) => item._id),
    datasets: [
      {
        data: reports.categoryReport.map((item) => item.totalExpense),
        backgroundColor: [
          "#FF5733",
          "#33FF57",
          "#5733FF",
          "#FFC300",
          "#FF9F00",
          "#DAF7A6",
          "#C70039",
        ], // Additional colors can be added as needed
      },
    ],
  };

  // Budget Allocation Chart Configuration
  const budgetAllocationChart = {
    labels: reports.budgetAllocation.map((item) => item.category),
    datasets: [
      {
        label: "Budget Allocation",
        data: reports.budgetAllocation.map((item) => item.allocatedAmount),
        backgroundColor: "#FF6347",
        borderColor: "#FF4500",
        borderWidth: 1,
      },
    ],
  };

  // Remaining Budget Chart Configuration
  const remainingBudgetChart = {
    labels: reports.remainingBudget.map((item) => item.category),
    datasets: [
      {
        label: "Remaining Budget",
        data: reports.remainingBudget.map((item) => item.remainingAmount),
        backgroundColor: "#4CAF50",
        borderColor: "#388E3C",
        borderWidth: 1,
      },
    ],
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Reports</h2>

      {/* Monthly Income Report */}
      {reports.monthlyIncome.length > 0 && (
        <div>
          <h3>Monthly Income Report</h3>
          <Line data={monthlyIncomeChart} />
        </div>
      )}

      {/* Category-wise Expense Report */}
      {reports.categoryReport.length > 0 && (
        <div>
          <h3>Category-wise Expense Report</h3>
          <Pie data={categoryExpenseChart} />
        </div>
      )}

      {/* Budget Allocation Report */}
      {reports.budgetAllocation.length > 0 && (
        <div>
          <h3>Budget Allocation</h3>
          <Pie data={budgetAllocationChart} />
        </div>
      )}

      {/* Remaining Budget Report */}
      {reports.remainingBudget.length > 0 && (
        <div>
          <h3>Remaining Budget</h3>
          <Pie data={remainingBudgetChart} />
        </div>
      )}
    </div>
  );
};

export default Reports;
