import React, { useState, useEffect } from "react";
import { Line, Pie } from "react-chartjs-2";
import axios from "axios";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, ArcElement, Title, Tooltip, Legend } from "chart.js";

// Register Chart.js components (including ArcElement for Pie charts)
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, ArcElement, Title, Tooltip, Legend);

const Reports = ({ userId }) => {
  const [reports, setReports] = useState({
    monthlyIncome: [],
    budgetAllocation: [],
    expenseReport: [],
    categoryReport: [],
    remainingBudget: [],
  });

  useEffect(() => {
    // Fetch reports data from backend
    const fetchReports = async () => {
      try {
        const incomeResponse = await axios.get(`/api/reports/monthly-income/${userId}`);
        const budgetResponse = await axios.get(`/api/reports/budget-allocation/${userId}`);
        const expenseResponse = await axios.get(`/api/reports/expense-report/${userId}`);
        const categoryResponse = await axios.get(`/api/reports/category-report/${userId}`);
        const remainingBudgetResponse = await axios.get(`/api/reports/remaining-budget/${userId}`);

        setReports({
          monthlyIncome: incomeResponse.data.data || [],
          budgetAllocation: budgetResponse.data.data || [],
          expenseReport: expenseResponse.data.data || [],
          categoryReport: categoryResponse.data.data || [],
          remainingBudget: remainingBudgetResponse.data.data || [],
        });
      } catch (error) {
        console.error("Error fetching reports:", error);
      }
    };

    if (userId) {
      fetchReports();
    }
  }, [userId]);

  // Monthly Income Chart Configuration
  const monthlyIncomeChart = {
    labels: reports.monthlyIncome.map((item) => item.date),
    datasets: [
      {
        label: "Monthly Income",
        data: reports.monthlyIncome.map((item) => item.amount),
        borderColor: "rgba(75,192,192,1)",
        fill: false,
      },
    ],
  };

  // Category-wise Expense Pie Chart Configuration
  const categoryExpenseChart = {
    labels: reports.categoryReport.map((item) => item._id),
    datasets: [
      {
        data: reports.categoryReport.map((item) => item.totalExpense),
        backgroundColor: ["#FF5733", "#33FF57", "#5733FF", "#FFC300", "#FF9F00"], // Additional colors can be added as needed
      },
    ],
  };

  return (
    <div>
      <h2>Reports</h2>

      {/* Monthly Income Report */}
      {reports.monthlyIncome.length > 0 && (
        <div>
          <h3>Monthly Income Report</h3>
          <Line data={monthlyIncomeChart} />
        </div>
      )}

      {/* Category-wise Expense Report */}
      {reports.categoryReport.length > 0 && (
        <div>
          <h3>Category-wise Expense Report</h3>
          <Pie data={categoryExpenseChart} />
        </div>
      )}

      {/* Add more charts for other reports like budget allocation, remaining budget, etc. */}
    </div>
  );
};

export default Reports;
