import React, { useState, useEffect } from "react";
import { Line, Pie } from "react-chartjs-2";
import '../assets/css/Reports.css';
import axios from "axios";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, ArcElement, Title, Tooltip, Legend } from "chart.js";

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, ArcElement, Title, Tooltip, Legend);

const Reports = () => {
  const [state, setState] = useState({
    reports: {
      monthlyIncome: [],
      budgetAllocation: [],
      expenseReport: [],
      categoryReport: [],
      remainingBudget: [],
    },
    userData: null,
    loading: false,
    error: "",
  });

  // Helper function to update state
  const updateState = (key, value) => setState((prev) => ({ ...prev, [key]: value }));

  // Fetch user details
  useEffect(() => {
    const fetchUserData = async () => {
      updateState("loading", true);
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("http://localhost:3001/api/auth/user-details", {
          headers: { Authorization: `Bearer ${token}` },
        });
        updateState("userData", res.data);
      } catch (err) {
        updateState("error", err.response?.data?.message || "Failed to fetch user details.");
      } finally {
        updateState("loading", false);
      }
    };

    fetchUserData();
  }, []);

  // Fetch reports
  useEffect(() => {
    const fetchReports = async () => {
      if (!state.userData) return;

      try {
        const token = localStorage.getItem("token");

        const responses = await Promise.all([
          axios.get("http://localhost:3001/api/reports/monthly-income", { headers: { Authorization: `Bearer ${token}` } }),
          axios.get("http://localhost:3001/api/reports/budget-allocation", { headers: { Authorization: `Bearer ${token}` } }),
          axios.get("http://localhost:3001/api/reports/expense-report", { headers: { Authorization: `Bearer ${token}` } }),
          axios.get("http://localhost:3001/api/reports/category-report", { headers: { Authorization: `Bearer ${token}` } }),
          axios.get("http://localhost:3001/api/reports/remaining-budget", { headers: { Authorization: `Bearer ${token}` } }),
        ]);

        updateState("reports", {
          monthlyIncome: Array.isArray(responses[0].data.data) ? responses[0].data.data : [],
          budgetAllocation: Array.isArray(responses[1].data.data) ? responses[1].data.data : [],
          expenseReport: Array.isArray(responses[2].data.data) ? responses[2].data.data : [],
          categoryReport: Array.isArray(responses[3].data.data) ? responses[3].data.data : [],
          remainingBudget: Array.isArray(responses[4].data.data) ? responses[4].data.data : [],
        });
      } catch (error) {
        updateState("error", "Error fetching reports");
      }
    };    

    fetchReports();
  }, [state.userData]);

  // Chart configurations
  const createPieChartData = (labels, data, color) => ({
    labels,
    datasets: [
      {
        data,
        backgroundColor: color,
        borderColor: color.map((c) => c.replace("0.5", "1")), // Adjust transparency
        borderWidth: 1,
      },
    ],
  });

  const createLineChartData = (labels, data, color) => ({
    labels,
    datasets: [
      {
        label: "Amount",
        data,
        fill: false,
        borderColor: color,
        tension: 0.1,
      },
    ],
  });

  const monthlyIncomeChart = createLineChartData(
    state.reports.monthlyIncome.map((entry) => entry.date),
    state.reports.monthlyIncome.map((entry) => entry.amount),
    "rgba(75,192,192,1)"
  );

  const categoryExpenseChart = createPieChartData(
    state.reports.categoryReport.map((entry) => entry.category),
    state.reports.categoryReport.map((entry) => entry.amount),
    ["rgba(255,99,132,0.2)", "rgba(53,162,235,0.5)"]
  );

  const budgetAllocationChart = createPieChartData(
    state.reports.budgetAllocation.map((entry) => entry.category),
    state.reports.budgetAllocation.map((entry) => entry.budget),
    ["rgba(75,192,192,0.2)", "rgba(255,205,86,0.5)"]
  );

  const remainingBudgetChart = state.reports.remainingBudget.length
  ? {
      labels: state.reports.remainingBudget.map((entry) => entry.category),
      datasets: [
        {
          label: "Remaining Budget",
          data: state.reports.remainingBudget.map((entry) => entry.remainingBudget),
          backgroundColor: ["rgba(54,162,235,0.5)", "rgba(153,102,255,0.5)"],
          borderColor: ["rgba(54,162,235,1)", "rgba(153,102,255,1)"],
          borderWidth: 1,
        },
      ],
    }
  : null;


  if (state.loading) return <p>Loading...</p>;
  if (state.error) return <p>{state.error}</p>;

  return (
    <div>
    <h2>Reports</h2>

    {/* Monthly Income Report */}
    {state.reports.monthlyIncome.length > 0 ? (
      <div className="chart-container">
        <h3>Monthly Income Report</h3>
        <Line data={monthlyIncomeChart} />
      </div>
    ) : (
      <p>No data available for Monthly Income.</p>
    )}

    {/* Category-wise Expense Report */}
    {state.reports.categoryReport.length > 0 ? (
      <div className="chart-container">
        <h3>Category-wise Expense Report</h3>
        <Pie data={categoryExpenseChart} />
      </div>
    ) : (
      <p>No data available for Category-wise Expenses.</p>
    )}

    {/* Budget Allocation Report */}
    {state.reports.budgetAllocation.length > 0 ? (
      <div className="chart-container">
        <h3>Budget Allocation</h3>
        <Pie data={budgetAllocationChart} />
      </div>
    ) : (
      <p>No data available for Budget Allocation.</p>
    )}

    {/* Remaining Budget Report */}
    {remainingBudgetChart ? (
      <div className="chart-container">
        <h3>Remaining Budget</h3>
        <Pie data={remainingBudgetChart} />
      </div>
    ) : (
      <p>No data available for Remaining Budget.</p>
    )}
  </div>

  );
};

export default Reports;
